import { Request, Response } from "express";
import TrainerModel from "../models/trainerModel";

export const TrainerController = {
  // ‚ûï ÿß€åÿ¨ÿßÿØ ÿ™ÿ±€åŸÜÿ± ÿ¨ÿØ€åÿØ
  createTrainer: async (req: Request, res: Response) => {
    try {
      const {
        name,
        lastName,
        province,
        department,
        specialty,
        email,
        phoneNumber,
      } = req.body;

      // ‚úÖ ÿßÿπÿ™ÿ®ÿßÿ±ÿ≥ŸÜÿ¨€å ÿßŸàŸÑ€åŸá (ÿ≠ÿØÿßŸÇŸÑ ŸÅ€åŸÑÿØŸáÿß€å ÿ∂ÿ±Ÿàÿ±€å)
      if (!name || !lastName || !province || !department || !specialty) {
        return res.status(400).json({
          message: "ŸÑÿ∑ŸÅÿßŸã ÿ™ŸÖÿßŸÖ ŸÅ€åŸÑÿØŸáÿß€å ÿ∂ÿ±Ÿàÿ±€å (ŸÜÿßŸÖÿå ÿ™ÿÆŸÑÿµÿå ŸàŸÑÿß€åÿ™ÿå ÿØ€åŸæÿßÿ±ÿ™ŸÖŸÜÿ™ÿå ÿ±ÿ¥ÿ™Ÿá) ÿ±ÿß ÿ™⁄©ŸÖ€åŸÑ ⁄©ŸÜ€åÿØ",
        });
      }

      // ‚úÖ ÿß⁄Øÿ± ÿß€åŸÖ€åŸÑ ÿ™⁄©ÿ±ÿßÿ±€å ŸÜÿ®ÿßÿ¥ÿØ (ÿßÿÆÿ™€åÿßÿ±€å)
      if (email) {
        const existing = await TrainerModel.findOne({ email });
        if (existing) {
          return res.status(409).json({ message: "ÿß€åŸÜ ÿß€åŸÖ€åŸÑ ŸÇÿ®ŸÑÿßŸã ÿ´ÿ®ÿ™ ÿ¥ÿØŸá ÿßÿ≥ÿ™" });
        }
      }

      const newTrainer = await TrainerModel.create(req.body);
      res.status(201).json({
        message: "ÿ™ÿ±€åŸÜÿ± ÿ®ÿß ŸÖŸàŸÅŸÇ€åÿ™ ÿß€åÿ¨ÿßÿØ ÿ¥ÿØ",
        data: newTrainer,
      });
    } catch (error) {
      console.error("‚ùå Error creating trainer:", error);
      res.status(500).json({ message: "ÿÆÿ∑ÿß ÿØÿ± ÿ´ÿ®ÿ™ ÿ™ÿ±€åŸÜÿ±" });
    }
  },

  // üìã ÿØÿ±€åÿßŸÅÿ™ ŸÑ€åÿ≥ÿ™ ÿ™ŸÖÿßŸÖ ÿ™ÿ±€åŸÜÿ±Ÿáÿß
  getAllTrainers: async (_req: Request, res: Response) => {
    try {
      const trainers = await TrainerModel.find().sort({ createdAt: -1 });
      res.status(200).json(trainers);
    } catch (error) {
      console.error("‚ùå Error fetching trainers:", error);
      res.status(500).json({ message: "ÿÆÿ∑ÿß ÿØÿ± ÿØÿ±€åÿßŸÅÿ™ ÿ™ÿ±€åŸÜÿ±Ÿáÿß" });
    }
  },

  // üîç ÿØÿ±€åÿßŸÅÿ™ €å⁄© ÿ™ÿ±€åŸÜÿ± ÿ®ÿ± ÿßÿ≥ÿßÿ≥ ID
  getTrainerById: async (req: Request, res: Response) => {
    try {
      const trainer = await TrainerModel.findById(req.params.id);
      if (!trainer) {
        return res.status(404).json({ message: "ÿ™ÿ±€åŸÜÿ± €åÿßŸÅÿ™ ŸÜÿ¥ÿØ" });
      }
      res.status(200).json(trainer);
    } catch (error) {
      console.error("‚ùå Error fetching trainer by ID:", error);
      res.status(500).json({ message: "ÿÆÿ∑ÿß ÿØÿ± ÿØÿ±€åÿßŸÅÿ™ ÿ™ÿ±€åŸÜÿ±" });
    }
  },

  // ‚úèÔ∏è ÿ®ÿ±Ÿàÿ≤ÿ±ÿ≥ÿßŸÜ€å ÿ™ÿ±€åŸÜÿ±
  updateTrainer: async (req: Request, res: Response) => {
    try {
      const updatedTrainer = await TrainerModel.findByIdAndUpdate(
        req.params.id,
        req.body,
        { new: true, runValidators: true }
      );

      if (!updatedTrainer) {
        return res.status(404).json({ message: "ÿ™ÿ±€åŸÜÿ± €åÿßŸÅÿ™ ŸÜÿ¥ÿØ" });
      }

      res.status(200).json({
        message: "ÿ™ÿ±€åŸÜÿ± ÿ®ÿß ŸÖŸàŸÅŸÇ€åÿ™ ÿ®ÿ±Ÿàÿ≤ÿ±ÿ≥ÿßŸÜ€å ÿ¥ÿØ",
        data: updatedTrainer,
      });
    } catch (error) {
      console.error("‚ùå Error updating trainer:", error);
      res.status(500).json({ message: "ÿÆÿ∑ÿß ÿØÿ± ÿ®ÿ±Ÿàÿ≤ÿ±ÿ≥ÿßŸÜ€å ÿ™ÿ±€åŸÜÿ±" });
    }
  },

  // üóëÔ∏è ÿ≠ÿ∞ŸÅ ÿ™ÿ±€åŸÜÿ±
  deleteTrainer: async (req: Request, res: Response) => {
    try {
      const deletedTrainer = await TrainerModel.findByIdAndDelete(req.params.id);
      if (!deletedTrainer) {
        return res.status(404).json({ message: "ÿ™ÿ±€åŸÜÿ± €åÿßŸÅÿ™ ŸÜÿ¥ÿØ" });
      }
      res.status(200).json({ message: "ÿ™ÿ±€åŸÜÿ± ÿ®ÿß ŸÖŸàŸÅŸÇ€åÿ™ ÿ≠ÿ∞ŸÅ ÿ¥ÿØ" });
    } catch (error) {
      console.error("‚ùå Error deleting trainer:", error);
      res.status(500).json({ message: "ÿÆÿ∑ÿß ÿØÿ± ÿ≠ÿ∞ŸÅ ÿ™ÿ±€åŸÜÿ±" });
    }
  },
};

