import mongoose, { Schema, Document, Types } from "mongoose";

// Ÿáÿ± ÿ±ÿØ€åŸÅ ÿßÿ≤ ÿ¨ÿØŸàŸÑ ŸÜŸÖÿ±ÿßÿ™
interface IScoreRow {
  exam1Written: number;
  exam1Practical: number;
  exam2Written: number;
  exam2Practical: number;
  finalWritten: number;
  finalPractical: number;
  total: number;
  teacherName: string;
}

// ÿßÿ∑ŸÑÿßÿπÿßÿ™ ÿ¥ÿÆÿµ€å
interface IPersonalInfo {
  residentName: string;
  fatherName: string;
  trainingYear: string;
  year: string;
  department: string;
}

export interface IEvaluationFormG extends Document {
