import { useState, useEffect } from "react";
import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Button } from "@/components/ui/button";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Teacher } from "@shared/schema";

interface EditTeacherModalProps {
  teacher: Teacher | null;
  open: boolean;
  onClose: () => void;
  onSave: (updatedTeacher: Teacher) => void;
}

export default function EditTeacherModal({ teacher, open, onClose, onSave }: EditTeacherModalProps) {
  const [formValues, setFormValues] = useState<Teacher | null>(teacher);

  // ŸàŸÇÿ™€å ÿßÿ≥ÿ™ÿßÿØ ÿ¨ÿØ€åÿØ ÿßŸÜÿ™ÿÆÿßÿ® ÿ¥ÿØÿå ŸÅÿ±ŸÖ ÿ±€åÿ≥ÿ™ ÿ¥ŸàÿØ
  useEffect(() => {
    setFormValues(teacher);
  }, [teacher]);

  if (!formValues) return null;

  const handleChange = (field: keyof Teacher, value: any) => {
    setFormValues(prev => prev ? { ...prev, [field]: value } : prev);
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (formValues) {
      onSave(formValues); // ÿßÿ±ÿ≥ÿßŸÑ ÿ®Ÿá ŸàÿßŸÑÿØ
      onClose();          // ÿ®ÿ≥ÿ™ŸÜ ŸÖŸàÿØÿßŸÑ
    }
  };

  const formatDate = (date: string | Date | undefined) => {
    if (!date) return "";
    const d = new Date(date);
    return d.toISOString().slice(0, 10); // YYYY-MM-DD ÿ®ÿ±ÿß€å input type="date"
  };

  return (
    <Dialog open={open} onOpenChange={onClose}>
      <DialogContent className="max-w-3xl max-h-[90vh] overflow-y-auto">
        <DialogHeader>
          <DialogTitle className="flex items-center space-x-4 rtl:space-x-reverse">
            <Avatar className="h-14 w-14">
              <AvatarImage src={formValues.profileImageUrl || ""} alt={formValues.name} />
              <AvatarFallback>{formValues.name.charAt(0)}</AvatarFallback>
            </Avatar>
            <span className="text-xl font-semibold">Ÿà€åÿ±ÿß€åÿ¥ ÿßÿ≥ÿ™ÿßÿØ</span>
          </DialogTitle>
        </DialogHeader>

        <form onSubmit={handleSubmit} className="grid grid-cols-2 gap-4 text-sm mt-4">
          <InputItem label="ŸÜÿßŸÖ" value={formValues.name} onChange={val => handleChange("name", val)} />
          {/* <InputItem label="ÿ™ÿÆŸÑÿµ" value={formValues.lostname} onChange={val => handleChange("lostname", val)} /> */}
          <InputItem label="ŸÜÿßŸÖ ŸæÿØÿ±" value={formValues.fatherName} onChange={val => handleChange("fatherName", val)} />
          <InputItem label="ŸÜÿßŸÖ ŸæÿØÿ± ⁄©ŸÑÿßŸÜ" value={formValues.grandfatherName} onChange={val => handleChange("grandfatherName", val)} />
          <InputItem label="ÿ±ÿ™ÿ®Ÿá ÿπŸÑŸÖ€å" value={formValues.academicRank} onChange={val => handleChange("academicRank", val)} />
          <InputItem
            label="ÿ™ÿßÿ±€åÿÆ ÿßÿÆÿ∞ ÿ±ÿ™ÿ®Ÿá"
            type="date"
            value={formatDate(formValues.rankAchievementDate)}
            onChange={val => handleChange("rankAchievementDate", val)}
          />
          <InputItem
            label="ÿ™ÿßÿ±€åÿÆ ÿ™ŸÇÿ±ÿ±€å ŸÖÿ±ÿ®€å"
            type="date"
            value={formatDate(formValues.trainerAppointmentDate)}
            onChange={val => handleChange("trainerAppointmentDate", val)}
          />
          <InputItem label="ÿ¨ŸÜÿ≥€åÿ™" value={formValues.gender} onChange={val => handleChange("gender", val)} />
          <InputItem label="ŸàŸÑÿß€åÿ™" value={formValues.province} onChange={val => handleChange("province", val)} />
          <InputItem label="ŸÖÿ∂ŸÖŸàŸÜ" value={formValues.subject} onChange={val => handleChange("subject", val)} />
          <InputItem label="Ÿàÿ∏€åŸÅŸá / ŸÖŸàŸÇŸÅ" value={formValues.position} onChange={val => handleChange("position", val)} />
          <InputItem label="ÿ¥ŸÅÿßÿÆÿßŸÜŸá" value={formValues.hospital} onChange={val => handleChange("hospital", val)} />
          <InputItem
            label="ÿ™ÿßÿ±€åÿÆ ÿ™ŸàŸÑÿØ"
            type="date"
            value={formatDate(formValues.dateOfBirth)}
            onChange={val => handleChange("dateOfBirth", val)}
          />
          <InputItem label="ŸÜŸÖÿ®ÿ± ÿ™ÿ∞⁄©ÿ±Ÿá" value={formValues.idNumber} onChange={val => handleChange("idNumber", val)} />
          <InputItem
            label="ÿ™ÿßÿ±€åÿÆ ÿ¥ÿ±Ÿàÿπ Ÿàÿ∏€åŸÅŸá"
            type="date"
            value={formatDate(formValues.dutyStartDate)}
            onChange={val => handleChange("dutyStartDate", val)}
          />
          <InputItem label="ÿ¥ŸÖÿßÿ±Ÿá ÿ™ŸÖÿßÿ≥" value={formValues.contactInfo} onChange={val => handleChange("contactInfo", val)} />
          <InputItem label="ŸÜŸÖÿ®ÿ± Ÿàÿßÿ™ÿ≥ÿßŸæ" value={formValues.whatsappNumber} onChange={val => handleChange("whatsappNumber", val)} />
          <InputItem label="ÿß€åŸÖ€åŸÑ" value={formValues.emailAddress} onChange={val => handleChange("emailAddress", val)} />
          <InputItem label="⁄©ŸàÿØ ŸæŸàÿ≥ÿ™" value={formValues.postCode} onChange={val => handleChange("postCode", val)} />
          <InputItem label="ŸÜŸàÿπ ÿ™ŸÇÿ±ÿ±€å" value={formValues.appointmentType} onChange={val => handleChange("appointmentType", val)} />
          {/* <InputItem label="ÿØ€åŸæÿßÿ±ÿ™ŸÖŸÜÿ™" value={formValues.department} onChange={val => handleChange("department", val)} /> */}
          {/* <InputItem label="ÿ™ÿ¨ÿ±ÿ®Ÿá ⁄©ÿßÿ±€å" type="number" value={formValues.experience} onChange={val => handleChange("experience", val)} /> */}
          <div className="col-span-2 flex justify-end space-x-2 rtl:space-x-reverse mt-4">
            <Button type="button" variant="outline" onClick={onClose}>ÿ®ÿ≥ÿ™ŸÜ</Button>
            <Button type="submit" variant="default">ÿ∞ÿÆ€åÿ±Ÿá</Button>
          </div>
        </form>
      </DialogContent>
    </Dialog>
  );
}

function InputItem({
  label,
  value,
  onChange,
  type = "text",
}: {
  label: string;
  value: any;
  onChange: (val: any) => void;
  type?: string;
}) {
  return (
    <div className="flex flex-col">
      <label className="text-gray-500 text-xs">{label}</label>
      <input
        type={type}
        className="border rounded px-2 py-1 text-sm"
        value={value || ""}
        onChange={e => onChange(e.target.value)}
      />
    </div>
  );
}

