import React, { useState, useEffect } from "react";
import { useTrainer } from "@/context/TrainerContext";

interface ScoreRow {
  score: string;
  teacherName: string;
}

export default function EvaluationFormE() {
  const { trainerId } = useTrainer();

  const [name, setName] = useState("");
  const [fatherName, setFatherName] = useState("");
  const [trainingYear, setTrainingYear] = useState("");
  const [incidentTitle, setIncidentTitle] = useState("");
  const [date, setDate] = useState("");
  const [averageScore, setAverageScore] = useState("");
  const [scores, setScores] = useState<ScoreRow[]>(
    Array.from({ length: 5 }, () => ({ score: "", teacherName: "" }))
  );

  const inputClass = "border rounded px-2 py-2 w-full text-center";

  // ŸÖÿ≠ÿßÿ≥ÿ®Ÿá ÿßŸàÿ≥ÿ∑ ŸÜŸÖÿ±ÿßÿ™
  useEffect(() => {
    const filled = scores.filter(
      (r) => r.score.trim() && r.teacherName.trim()
    );

    if (filled.length === 0) {
      setAverageScore("");
      return;
    }

    const sum = filled.reduce(
      (acc, r) => acc + (parseFloat(r.score) || 0),
      0
    );
    const avg = sum / filled.length;
    setAverageScore(avg.toFixed(2));
  }, [scores]);

  // ÿßÿ±ÿ≥ÿßŸÑ ŸÅÿ±ŸÖ
  const handleSubmit = async () => {
    if (!trainerId) {
      alert("Trainer ID ŸÖŸàÿ¨ŸàÿØ ŸÜ€åÿ≥ÿ™ÿå ŸÅÿ±ŸÖ ÿßÿ±ÿ≥ÿßŸÑ ŸÜŸÖ€å‚Äåÿ¥ŸàÿØ!");
      return;
    }

    if (
      !name.trim() ||
      !fatherName.trim() ||
      !trainingYear.trim() ||
      !incidentTitle.trim() ||
      !date.trim()
    ) {
      alert("ŸÑÿ∑ŸÅÿßŸã ÿ™ŸÖÿßŸÖ ŸÅ€åŸÑÿØŸáÿß€å ÿ∂ÿ±Ÿàÿ±€å ÿ±ÿß Ÿæÿ± ⁄©ŸÜ€åÿØ!");
      return;
    }

    // ÿ®ÿ±ÿ±ÿ≥€å ÿß€åŸÜ⁄©Ÿá ŸÜ€åŸÖŸá‚ÄåŸæÿ± ŸÜÿ®ÿßÿ¥ŸÜÿØ
    for (let i = 0; i < scores.length; i++) {
      const r = scores[i];
      if (
        (r.score.trim() && !r.teacherName.trim()) ||
        (!r.score.trim() && r.teacherName.trim())
      ) {
        alert(`ŸÑÿ∑ŸÅÿßŸã ÿ±ÿØ€åŸÅ ÿ¥ŸÖÿßÿ±Ÿá ${i + 1} ÿ±ÿß ⁄©ÿßŸÖŸÑ Ÿæÿ± ⁄©ŸÜ€åÿØ €åÿß ÿÆÿßŸÑ€å ÿ®⁄Øÿ∞ÿßÿ±€åÿØ.`);
        return;
      }
    }

    const validScores = scores
      .filter((r) => r.score.trim() && r.teacherName.trim())
      .map((r) => ({
        score: r.score.trim(),
        teacherName: r.teacherName.trim(),
      }));

    try {
      const res = await fetch("http://localhost:5000/api/evaluationFormE", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          trainer: trainerId,
          residentName: name.trim(),
          fatherName: fatherName.trim(),
          trainingYear: trainingYear.trim(),
          incidentTitle: incidentTitle.trim(),
          date: date.trim(),
          averageScore: averageScore.trim(),
          scores: validScores,
        }),
      });

      if (res.ok) {
        alert("ŸÅÿ±ŸÖ ÿ®ÿß ŸÖŸàŸÅŸÇ€åÿ™ ÿ∞ÿÆ€åÿ±Ÿá ÿ¥ÿØ!");
        // ÿ±€åÿ≥ÿ™ ŸÅÿ±ŸÖ
        setName("");
        setFatherName("");
        setTrainingYear("");
        setIncidentTitle("");
        setDate("");
        setAverageScore("");
        setScores(Array.from({ length: 5 }, () => ({ score: "", teacherName: "" })));
      } else {
        const data = await res.json().catch(() => null);
        alert(data?.message || "ÿÆÿ∑ÿß ÿØÿ± ÿ∞ÿÆ€åÿ±Ÿá ŸÅÿ±ŸÖ");
      }
    } catch (err) {
      console.error(err);
      alert("ÿÆÿ∑ÿß ÿØÿ± ÿ∞ÿÆ€åÿ±Ÿá ŸÅÿ±ŸÖ");
    }
  };

  return (
    <div className="max-w-5xl mx-auto bg-white shadow-lg rounded-2xl p-6 mt-6">
      <h2 className="text-xl font-bold text-center mb-4">
        ŸÅÿ±ŸÖ ÿßÿ±ÿ≤ÿ¥€åÿßÿ®€å ÿ≥ÿßŸÑÿßŸÜŸá ÿØÿ≥ÿ™€åÿßÿ±
      </h2>

      {/* ÿßÿ∑ŸÑÿßÿπÿßÿ™ Ÿæÿß€åŸá */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
        <input
          type="text"
          placeholder="ÿßÿ≥ŸÖ"
          value={name}
          onChange={(e) => setName(e.target.value)}
          className={inputClass}
        />
        <input
          type="text"
          placeholder="ŸàŸÑÿØ"
          value={fatherName}
          onChange={(e) => setFatherName(e.target.value)}
          className={inputClass}
        />
        <input
          type="text"
          placeholder="ÿ≥ÿßŸÑ ÿ™ÿ±€åŸÜŸÜ⁄Ø"
          value={trainingYear}
          onChange={(e) => setTrainingYear(e.target.value)}
          className={inputClass}
        />
        <input
          type="text"
          placeholder="ÿ™ÿßÿ±€åÿÆ"
          value={date}
          onChange={(e) => setDate(e.target.value)}
          className={inputClass}
        />
      </div>

      {/* ÿ¨ÿØŸàŸÑ */}
      <table className="table-auto border-collapse border w-full text-center mb-6">
        <thead>
          <tr>
            <th className="border px-2 py-2">ÿπŸÜŸàÿßŸÜ ŸàÿßŸÇÿπŸá</th>
            <th className="border px-2 py-2">ŸÜŸÖÿ±Ÿá ÿØÿßÿØŸá ÿ¥ÿØŸá</th>
            <th className="border px-2 py-2">ÿßÿ≥ŸÖ ÿßÿ≥ÿ™ÿßÿØ</th>
          </tr>
        </thead>
        <tbody>
          {scores.map((row, idx) => (
            <tr key={idx}>
              {idx === 0 && (
                <td rowSpan={scores.length + 1}>
                  <textarea
                    placeholder="ÿπŸÜŸàÿßŸÜ ŸàÿßŸÇÿπŸá"
                    rows={10}
                    value={incidentTitle}
                    onChange={(e) => setIncidentTitle(e.target.value)}
                    className={inputClass + " h-full resize-none"}
                  />
                </td>
              )}
              <td>
                <input
                  type="text"
                  placeholder="ŸÜŸÖÿ±Ÿá"
                  className={inputClass}
                  value={row.score}
                  onChange={(e) => {
                    const newRows = [...scores];
                    newRows[idx].score = e.target.value;
                    setScores(newRows);
                  }}
                />
              </td>
              <td>
                <input
                  type="text"
                  placeholder="ÿßÿ≥ŸÖ ÿßÿ≥ÿ™ÿßÿØ"
                  className={inputClass}
                  value={row.teacherName}
                  onChange={(e) => {
                    const newRows = [...scores];
                    newRows[idx].teacherName = e.target.value;
                    setScores(newRows);
                  }}
                />
              </td>
            </tr>
          ))}

          {/* ÿßŸàÿ≥ÿ∑ ŸÜŸÖÿ±ÿßÿ™ */}
          <tr>
            <td colSpan={2} className="border px-2 py-2 font-bold">
              ÿßŸàÿ≥ÿ∑ ŸÜŸÖÿ±ÿßÿ™
            </td>
            <td>
              <input
                type="text"
                value={averageScore}
                readOnly
                className={inputClass + " bg-gray-100"}
              />
            </td>
          </tr>
        </tbody>
      </table>

      {/* ÿØ⁄©ŸÖŸá ÿ∞ÿÆ€åÿ±Ÿá */}
      <div className="text-center mt-4">
        <button
          onClick={handleSubmit}
          className="px-6 py-2 bg-green-600 text-white rounded-xl hover:bg-green-700 transition"
        >
          ÿ∞ÿÆ€åÿ±Ÿá ŸÅÿ±ŸÖ
        </button>
      </div>
    </div>
  );
}

