import { TeacherActivityModel } from "../models/form-J"; // ŸÖÿØŸÑ ÿßÿµŸÑÿßÿ≠ ÿ¥ÿØŸá

export class TeacherActivityController {
  static async create(req, res) {
    try {
      const { trainerId, teachers, activities } = req.body;

      if (!trainerId) {
        return res.status(400).json({ message: "TrainerId ÿßŸÑÿ≤ÿßŸÖ€å ÿßÿ≥ÿ™" });
      }

      const newForm = new TeacherActivityModel({ trainerId, teachers, activities });
      await newForm.save();
      res.status(201).json(newForm);
    } catch (err) {
      res.status(500).json({ message: "ÿÆÿ∑ÿß ÿØÿ± ÿ∞ÿÆ€åÿ±Ÿá ŸÅÿ±ŸÖ", error: err });
    }
  }


  static async getAll(req, res) {
    try {
      const { trainerId } = req.query; // üîπ ÿßÿ≤ query ÿ®⁄Ø€åÿ±
      if (!trainerId) {
        return res.status(400).json({ message: "TrainerId ÿßŸÑÿ≤ÿßŸÖ€å ÿßÿ≥ÿ™" });
      }

      const forms = await TeacherActivityModel.find({ trainerId }).sort({ createdAt: -1 });
      res.json(forms);
    } catch (err) {
      res.status(500).json({ message: "ÿÆÿ∑ÿß ÿØÿ± ÿØÿ±€åÿßŸÅÿ™ ÿØÿßÿØŸá‚ÄåŸáÿß", error: err });
    }
  }


  static async update(req, res) {
    try {
      const { id } = req.params;
      const updated = await TeacherActivityModel.findByIdAndUpdate(id, req.body, { new: true });
      if (!updated) return res.status(404).json({ message: "ŸÅÿ±ŸÖ €åÿßŸÅÿ™ ŸÜÿ¥ÿØ" });
      res.json(updated);
    } catch (err) {
      res.status(500).json({ message: "ÿÆÿ∑ÿß ÿØÿ± ÿ®ÿ±Ÿàÿ≤ÿ±ÿ≥ÿßŸÜ€å ŸÅÿ±ŸÖ", error: err });
    }
  }
}

