      const gregorianDate = lecture.date?.toDate(); // ÿ™ÿ®ÿØ€åŸÑ ÿ®Ÿá JavaScript Date (ŸÖ€åŸÑÿßÿØ€å)
      const dateString = gregorianDate ? gregorianDate.toISOString().split('T')[0] : "";

