import { useEffect, useState } from "react";
import axios from "axios";
import { User } from "@shared/schema";
import Sidebar from "@/components/layout/sidebar";
import Header from "@/components/layout/header";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from "@/components/ui/card";
import { useToast } from "@/hooks/use-toast";
import { Plus, Search } from "lucide-react";
import { Input } from "@/components/ui/input";
import UserFormDialog from "@/components/forms/UserFormDialog";
import { useAuth } from "@/hooks/useAuth";

export default function UserManagement() {
  const { user } = useAuth();
  const { toast } = useToast();
  const [users, setUsers] = useState<User[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [searchTerm, setSearchTerm] = useState("");
  const [isUserDialogOpen, setIsUserDialogOpen] = useState(false);
  const [editingUser, setEditingUser] = useState<User | null>(null);




  // ÿØÿ±€åÿßŸÅÿ™ ⁄©ÿßÿ±ÿ®ÿ±ÿßŸÜ ÿßÿ≤ ÿ≥ÿ±Ÿàÿ±
  const fetchUsers = async () => {
    try {
      setLoading(true);
      const res = await axios.get<User[]>("/api/users");
      setUsers(res.data);
    } catch (err: any) {
      setError(err.message || "ÿÆÿ∑ÿß ÿØÿ± ÿØÿ±€åÿßŸÅÿ™ ⁄©ÿßÿ±ÿ®ÿ±ÿßŸÜ");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchUsers();
  }, []);

  // ÿ≠ÿ∞ŸÅ ⁄©ÿßÿ±ÿ®ÿ±
  const deleteUser = async (id: string) => {
    if (!confirm("ÿ¢€åÿß ŸÖÿ∑ŸÖÿ¶ŸÜ Ÿáÿ≥ÿ™€åÿØ ⁄©Ÿá ŸÖ€å‚ÄåÿÆŸàÿßŸá€åÿØ ÿß€åŸÜ ⁄©ÿßÿ±ÿ®ÿ± ÿ±ÿß ÿ≠ÿ∞ŸÅ ⁄©ŸÜ€åÿØÿü")) return;
    try {
      await axios.delete(`/api/users/${id}`);
      setUsers((prev) => prev.filter((u) => u._id !== id));
      toast({ title: "ŸÖŸàŸÅŸÇ€åÿ™", description: "⁄©ÿßÿ±ÿ®ÿ± ÿ≠ÿ∞ŸÅ ÿ¥ÿØ" });
    } catch (err: any) {
      toast({ title: "ÿÆÿ∑ÿß", description: "ÿ≠ÿ∞ŸÅ ⁄©ÿßÿ±ÿ®ÿ± ÿ®ÿß ÿÆÿ∑ÿß ŸÖŸàÿßÿ¨Ÿá ÿ¥ÿØ", variant: "destructive" });
    }
  };

// ŸÅ€åŸÑÿ™ÿ± ÿ¨ÿ≥ÿ™ÿ¨Ÿà
const filteredUsers = users.filter(
  (u) =>
    u.firstName?.toLowerCase().includes(searchTerm.toLowerCase()) ||
    u.email?.toLowerCase().includes(searchTerm.toLowerCase())
);


  const handleAddUser = () => {
    setEditingUser(null);
    setIsUserDialogOpen(true);
  };

  const handleEditUser = (user: User) => {
    setEditingUser(user);
    setIsUserDialogOpen(true);
  };

  const handleCloseDialog = () => {
    setIsUserDialogOpen(false);
    setEditingUser(null);
  };

  return (
    <div className="min-h-screen bg-slate-50">
      <Header />
      <Sidebar />
      <div className="mr-64 pt-20 p-6 space-y-6">
        {/* Header */}
        <div className="flex items-center justify-between">
          <div>
            <h1 className="text-3xl font-bold tracking-tight">ŸÖÿØ€åÿ±€åÿ™ ⁄©ÿßÿ±ÿ®ÿ±ÿßŸÜ</h1>
            <p className="text-slate-600 dark:text-slate-400">ŸÖÿØ€åÿ±€åÿ™ ÿ≠ÿ≥ÿßÿ®‚ÄåŸáÿß€å ⁄©ÿßÿ±ÿ®ÿ±ÿßŸÜ ÿ≥€åÿ≥ÿ™ŸÖ</p>
          </div>
          {user?.role === "admin" && (
            <Button
              onClick={handleAddUser}
              data-testid="button-add-user"
            >
              <Plus className="h-4 w-4 mr-2" />
              ÿßŸÅÿ≤ŸàÿØŸÜ ⁄©ÿßÿ±ÿ®ÿ± ÿ¨ÿØ€åÿØ
            </Button>
          )}
        </div>

        {/* Main Card */}
        <Card>
          <CardHeader>
            <CardTitle>ŸÑ€åÿ≥ÿ™ ⁄©ÿßÿ±ÿ®ÿ±ÿßŸÜ</CardTitle>
            <CardDescription>ŸÖÿ¨ŸÖŸàÿπ {filteredUsers.length} ⁄©ÿßÿ±ÿ®ÿ±</CardDescription>
          </CardHeader>
          <CardContent>
            {/* Search */}
            <div className="mb-6">
              <div className="relative">
                <Search className="absolute left-3 top-3 h-4 w-4 text-slate-400" />
                <Input
                  placeholder="ÿ¨ÿ≥ÿ™ÿ¨Ÿà ÿ®ÿ± ÿßÿ≥ÿßÿ≥ ŸÜÿßŸÖ €åÿß ÿß€åŸÖ€åŸÑ..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10"
                />
              </div>
            </div>

            {/* Content */}
            {loading ? (
              <div className="flex justify-center py-8">
                <div className="animate-spin rounded-full h-8 w-8 border-b-2 border-hospital-green-500"></div>
              </div>
            ) : error ? (
              <p className="text-red-600">{error}</p>
            ) : filteredUsers.length === 0 ? (
              <div className="text-center py-8 text-slate-500">
                {searchTerm ? "Ÿá€å⁄Ü ⁄©ÿßÿ±ÿ®ÿ±€å €åÿßŸÅÿ™ ŸÜÿ¥ÿØ" : "ŸáŸÜŸàÿ≤ ⁄©ÿßÿ±ÿ®ÿ±€å ÿßÿ∂ÿßŸÅŸá ŸÜÿ¥ÿØŸá ÿßÿ≥ÿ™"}
              </div>
            ) : (
              <div className="overflow-x-auto">
                <table className="w-full border-collapse rounded-lg overflow-hidden shadow-sm">
                  <thead className="bg-slate-100 text-slate-700">
                    <tr>
                      <th className="border p-2 text-center">ŸÜÿßŸÖ</th>
                      <th className="border p-2 text-center">ÿß€åŸÖ€åŸÑ</th>
                      <th className="border p-2 text-center">ŸÜŸÇÿ¥</th>
                      <th className="border p-2 text-center">ÿπŸÖŸÑ€åÿßÿ™</th>
                    </tr>
                  </thead>
                  <tbody>
                    {filteredUsers.map((userItem) => (
                      <tr key={userItem._id} className="text-center hover:bg-slate-50">
                      <td className="border p-2">{userItem.firstName ?? "-"}</td>
                      <td className="border p-2">{userItem.email ?? "-"}</td>
                      <td className="border p-2">{userItem.role ?? "-"}</td>

                        <td className="border p-2 flex items-center justify-center gap-2">
                          {user?.role === "admin" && (
                            <>
                              <Button
                                variant="outline"
                                size="sm"
                                onClick={() => handleEditUser(userItem)}
                              >
                                Ÿà€åÿ±ÿß€åÿ¥
                              </Button>
                              <Button
                                variant="destructive"
                                size="sm"
                                onClick={() => deleteUser(userItem._id)}
                              >
                                ÿ≠ÿ∞ŸÅ
                              </Button>
                            </>
                          )}
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}
          </CardContent>
        </Card>
      </div>

      <UserFormDialog
        isOpen={isUserDialogOpen}
        onClose={handleCloseDialog}
        onSuccess={fetchUsers}
        editingUser={editingUser}
      />
    </div>
  );
}

