import React, { useState, useMemo } from "react";
import { useTrainer } from "@/context/TrainerContext"; // üëà ÿßÿ∂ÿßŸÅŸá ⁄©ŸÜ

const RotationForm: React.FC = () => {
  const { trainerId } = useTrainer(); // trainerId ŸÖÿ≥ÿ™ŸÇ€åŸÖ ÿßÿ≤ context
  /*** üîπ ÿßÿ∑ŸÑÿßÿπÿßÿ™ ÿ®ÿßŸÑÿß€å ŸÅÿ±ŸÖ (General Info) ***/
  const [header, setHeader] = useState({
    studentName: "",
    department: "",
    rotationFrom: "",
    rotationTo: "",
    date: "",
  });
  const [selectedTrainerId, setSelectedTrainerId] = useState<string>("");
  // ÿß€åŸÜ ŸÖŸÇÿØÿßÿ± ÿ±ÿß ŸÖ€å‚Äåÿ™ŸàÿßŸÜ€å ÿßÿ≤ dropdown €åÿß ŸÑ€åÿ≥ÿ™ ÿ™ÿ±€åŸÜÿ±Ÿáÿß ÿ®⁄Ø€åÿ±€å

  const handleHeaderChange = (field: string, value: string) => {
    setHeader({ ...header, [field]: value });
  };

  /*** üîπ ÿ¨ÿØŸàŸÑ ŸÅÿßÿ±ÿ≥€å ***/
  const persianTopics = [
    "ÿßÿ¥ÿ™ÿ±ÿß⁄© ÿØÿ± ⁄©ŸÜŸÅÿ±ÿßŸÜÿ≥",
    "ÿßÿ¥ÿ™ÿ±ÿß⁄© ÿØÿ± ÿ™ÿØÿ±€åÿ≥/ÿ≥ŸÖ€åŸÜÿßÿ±",
    "⁄©ÿßÿ±Ÿáÿß€å ÿπŸÖŸÑ€å Ÿà ÿ™€åŸàÿ±€å",
    "ÿßÿÆŸÑÿßŸÇ ÿ∑ÿ®€å",
    "ÿ≠ŸÅÿ∏ ŸÜÿ∏ŸÖ/ÿßÿ¥ÿ™ÿ±ÿß⁄©",
  ];
  const [persianRows, setPersianRows] = useState(
    persianTopics.map(() => ({
      mark: "",
      teacherName: "",
      teacherSign: "",
      note: "",
    }))
  );
  const [persianNote, setPersianNote] = useState("");

  const handlePersianChange = (row: number, field: string, value: string) => {
    const updated = [...persianRows];
    (updated as any)[row][field] = value;
    setPersianRows(updated);
  };

  /*** üîπ ÿ¨ÿØŸàŸÑ ÿßŸÜ⁄ØŸÑ€åÿ≥€å Ÿæÿß€å€åŸÜ (Rotation Competencies) ***/
  const [rotationName, setRotationName] = useState("");

  const englishCompetencies = [
    "Describe basics of radiographic & MRI",
    "Demonstrate indications for MRI",
    "Describe anatomy of skull base",
    "Demonstrate interpretation of chest imaging",
    "Describe interpretation of brain & orbit CT",
    "Demonstrate interpretation of MRI brain",
    "Recognize common artifacts in MRI",
  ];

  // Ÿáÿ± ÿ±ÿØ€åŸÅ ‚Üí 4 ŸáŸÅÿ™Ÿáÿå Ÿáÿ± ŸáŸÅÿ™Ÿá ÿ¥ÿßŸÖŸÑ cases Ÿà level
  const [rows, setRows] = useState(
    englishCompetencies.map(() => ({
      weeks: [
        { cases: "", level: "" },
        { cases: "", level: "" },
        { cases: "", level: "" },
        { cases: "", level: "" },
      ],
      total: 0,
    }))
  );

  const handleEnglishChange = (
    rowIndex: number,
    weekIndex: number,
    field: "cases" | "level",
    value: string
  ) => {
    const updated = [...rows];
    updated[rowIndex].weeks[weekIndex][field] = value;

    // ŸÖÿ≠ÿßÿ≥ÿ®Ÿá ŸÖÿ¨ŸÖŸàÿπ Ÿáÿ± ÿ±ÿØ€åŸÅ
    const total = updated[rowIndex].weeks.reduce((sum, w) => {
      const c = parseInt(w.cases, 10);
      return sum + (isNaN(c) ? 0 : c);
    }, 0);
    updated[rowIndex].total = total;
    setRows(updated);
  };

  const grandTotal = useMemo(
    () => rows.reduce((s, r) => s + r.total, 0),
    [rows]
  );

  /*** üîπ ÿ∞ÿÆ€åÿ±Ÿá ŸÅÿ±ŸÖ ***/
  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!trainerId) {
      alert("‚ö†Ô∏è ŸÑÿ∑ŸÅÿßŸã ÿßÿ®ÿ™ÿØÿß €å⁄© ÿ™ÿ±€åŸÜÿ± ÿ´ÿ®ÿ™ ⁄©ŸÜ€åÿØ €åÿß ÿßŸÜÿ™ÿÆÿßÿ® ⁄©ŸÜ€åÿØ");
      return;
    }

    const payload = {
      trainerId, // üëà trainerId ÿßÿ≤ Context
      header,
      persianRows,
      persianNote,
      rotationName,
      rows,
    };

    console.log("üì¶ ÿßÿ±ÿ≥ÿßŸÑ ÿ®Ÿá ÿ≥ÿ±Ÿàÿ±:", payload);

    try {
      await fetch(`http://localhost:5000/api/rotation-form`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });
      alert("‚úÖ ŸÅÿ±ŸÖ ÿ®ÿß ŸÖŸàŸÅŸÇ€åÿ™ ÿ∞ÿÆ€åÿ±Ÿá ÿ¥ÿØ");
    } catch (err) {
      console.error(err);
      alert("‚ùå ÿÆÿ∑ÿß ÿØÿ± ÿ∞ÿÆ€åÿ±Ÿá ŸÅÿ±ŸÖ");
    }
  };

  return (
    <form
      onSubmit={handleSubmit}
      className="max-w-7xl mx-auto p-6 space-y-10 text-sm"
    >
      {/* ===================== üîπ ÿ®ÿÆÿ¥ ÿßŸàŸÑ: ÿßÿ∑ŸÑÿßÿπÿßÿ™ ÿπŸÖŸàŸÖ€å ===================== */}
      <div className="border p-4 rounded-lg shadow grid grid-cols-2 gap-4">
        <input
          className="border p-2"
          placeholder="ŸÜÿßŸÖ ŸÖÿ≠ÿµŸÑ / Student Name"
          value={header.studentName}
          onChange={(e) => handleHeaderChange("studentName", e.target.value)}
        />
        <input
          className="border p-2"
          placeholder="ÿØ€åŸæÿßÿ±ÿ™ŸÖŸÜÿ™ / Department"
          value={header.department}
          onChange={(e) => handleHeaderChange("department", e.target.value)}
        />
        <input
          className="border p-2"
          placeholder="Rotation From"
          value={header.rotationFrom}
          onChange={(e) => handleHeaderChange("rotationFrom", e.target.value)}
        />
        <input
          className="border p-2"
          placeholder="Rotation To"
          value={header.rotationTo}
          onChange={(e) => handleHeaderChange("rotationTo", e.target.value)}
        />
        <input
          className="border p-2 col-span-2"
          placeholder="Date / ÿ™ÿßÿ±€åÿÆ"
          value={header.date}
          onChange={(e) => handleHeaderChange("date", e.target.value)}
        />
      </div>

      {/* ===================== üîπ ÿ¨ÿØŸàŸÑ ŸÅÿßÿ±ÿ≥€å ===================== */}
      <div className="border p-4 rounded-lg shadow">
        <h2 className="font-bold mb-4 text-right">ŸÅŸàÿ±ŸÖ ÿßÿ±ÿ≤€åÿßÿ®€å ÿßÿ≥ÿ™€åÿ¨</h2>
        <table className="w-full border text-right">
          <thead>
            <tr className="bg-gray-100">
              <th className="border p-2">ŸÖŸàÿ∂Ÿàÿπ ⁄©ŸÜŸÅÿ±ÿßŸÜÿ≥</th>
              <th className="border p-2">ŸÜŸÖÿ±Ÿá</th>
              <th className="border p-2">ŸÜÿßŸÖ ÿßÿ≥ÿ™ÿßÿØ</th>
              <th className="border p-2">ÿßŸÖÿ∂ÿß</th>
              <th className="border p-2">ŸÖŸÑÿßÿ≠ÿ∏ÿßÿ™</th>
            </tr>
          </thead>
          <tbody>
            {persianRows.map((row, i) => (
              <tr key={i}>
                <td className="border p-2">{persianTopics[i]}</td>
                {["mark", "teacherName", "teacherSign", "note"].map((f) => (
                  <td key={f} className="border p-1">
                    <input
                      className="w-full border p-1"
                      value={(row as any)[f]}
                      onChange={(e) =>
                        handlePersianChange(i, f, e.target.value)
                      }
                    />
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>

        {/* €åÿßÿØÿØÿßÿ¥ÿ™ Ÿæÿß€å€åŸÜ ÿ¨ÿØŸàŸÑ */}
        <div className="mt-4">
          <label className="block mb-2 text-right font-medium">
            €åÿßÿØÿØÿßÿ¥ÿ™ (ŸÖÿ´ŸÑÿßŸã: ÿßÿ≤ 5% ŸÜŸÖÿ±Ÿá ÿØÿßÿØŸá ÿ¥ÿØŸá ÿßÿ≥ÿ™)
          </label>
          <textarea
            className="w-full border p-2 rounded-lg text-right"
            rows={3}
            value={persianNote}
            onChange={(e) => setPersianNote(e.target.value)}
          />
        </div>
      </div>

      {/* ===================== üîπ ÿ¨ÿØŸàŸÑ ÿßŸÜ⁄ØŸÑ€åÿ≥€å ===================== */}
      <div className="border p-4 rounded-lg shadow">
        <label className="block mb-4 font-bold">Rotation Name</label>
        <input
          className="border p-2 w-full mb-6"
          placeholder="Enter rotation name..."
          value={rotationName}
          onChange={(e) => setRotationName(e.target.value)}
        />

        <h2 className="font-bold mb-4">Rotation Competencies</h2>
        <div className="overflow-x-auto">
          <table className="w-full border text-center">
            <thead>
              <tr className="bg-gray-100">
                <th rowSpan={2} className="border p-2 text-left">
                  Competence
                </th>
                {["1st", "2nd", "3rd", "4th"].map((w) => (
                  <th key={w} colSpan={2} className="border p-2">
                    {w} Week
                  </th>
                ))}
                <th rowSpan={2} className="border p-2">
                  Total of Cases
                </th>
              </tr>
              <tr className="bg-gray-100">
                {Array.from({ length: 4 }).map((_, i) => (
                  <React.Fragment key={i}>
                    <th className="border p-1">Cases</th>
                    <th className="border p-1">Level</th>
                  </React.Fragment>
                ))}
              </tr>
            </thead>

            <tbody>
              {rows.map((row, ri) => (
                <tr key={ri}>
                  <td className="border p-2 text-left">
                    {englishCompetencies[ri]}
                  </td>
                  {row.weeks.map((w, wi) => (
                    <React.Fragment key={wi}>
                      <td className="border p-1">
                        <input
                          type="number"
                          min={0}
                          className="w-full border p-1 text-center"
                          value={w.cases}
                          onChange={(e) =>
                            handleEnglishChange(ri, wi, "cases", e.target.value)
                          }
                        />
                      </td>
                      <td className="border p-1">
                        <select
                          className="w-full border p-1 text-center"
                          value={w.level}
                          onChange={(e) =>
                            handleEnglishChange(ri, wi, "level", e.target.value)
                          }
                        >
                          <option value="">--</option>
                          <option value="Basic">Basic</option>
                          <option value="Intermediate">Intermediate</option>
                          <option value="Advanced">Advanced</option>
                        </select>
                      </td>
                    </React.Fragment>
                  ))}
                  <td className="border p-1 bg-gray-50 font-bold">
                    {row.total}
                  </td>
                </tr>
              ))}
            </tbody>

            <tfoot>
              <tr className="bg-gray-100 font-bold">
                <td className="border p-2 text-right" colSpan={9}>
                  Grand Total of Cases
                </td>
                <td className="border p-2">{grandTotal}</td>
              </tr>
            </tfoot>
          </table>
        </div>
      </div>

      {/* ÿØ⁄©ŸÖŸá ÿ∞ÿÆ€åÿ±Ÿá */}
      <button
        type="submit"
        className="bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700"
      >
        ÿ∞ÿÆ€åÿ±Ÿá ŸÅÿ±ŸÖ
      </button>
    </form>
  );
};

export default RotationForm;

