import { Dialog, DialogContent, DialogHeader, DialogTitle } from "@/components/ui/dialog";
import { Avatar, AvatarFallback, AvatarImage } from "@/components/ui/avatar";
import { Badge } from "@/components/ui/badge";
import { Teacher } from "@shared/schema";
import { Button } from "@/components/ui/button";

interface ViewTeacherModalProps {
  teacher: Teacher | null;
  open: boolean;
  onClose: () => void;
}

export default function ViewTeacherModal({ teacher, open, onClose }: ViewTeacherModalProps) {
  if (!teacher) return null;

  const formatDate = (date: string | Date | undefined) => {
    if (!date) return "ŸÜÿßŸÖÿ¥ÿÆÿµ";
    try {
      return new Date(date).toLocaleDateString("fa-AF");
    } catch {
      return "ŸÜÿßŸÖÿ¥ÿÆÿµ";
    }
  };

  const handlePrint = () => {
    window.print();
  };

  return (
    <Dialog open={open} onOpenChange={onClose}>
      <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto rounded-2xl border border-slate-300 shadow-xl bg-white dark:bg-slate-900">
        <DialogHeader>
          <DialogTitle className="flex items-center gap-4">
            <Avatar className="h-16 w-16 ring-2 ring-indigo-500">
              <AvatarImage src={teacher.profileImageUrl || ""} alt={teacher.name} />
              <AvatarFallback>{teacher.name.charAt(0)}</AvatarFallback>
            </Avatar>
            <div>
              <h2 className="text-2xl font-bold text-slate-900 dark:text-white">
                {teacher.name} {teacher.lostname}
              </h2>
            </div>
          </DialogTitle>
        </DialogHeader>

        {/* ÿ¨ÿ≤ÿ¶€åÿßÿ™ ÿßÿ≥ÿ™ÿßÿØ */}
        <div className="grid grid-cols-2 gap-6 text-sm mt-6">
          <InfoItem label="ŸÜÿßŸÖ" value={teacher.name} />
          <InfoItem label="ÿ™ÿÆŸÑÿµ" value={teacher.lostname} />
          <InfoItem label="ŸÜÿßŸÖ ŸæÿØÿ±" value={teacher.fatherName} />
          <InfoItem label="ŸÜÿßŸÖ ŸæÿØÿ± ⁄©ŸÑÿßŸÜ" value={teacher.grandfatherName} />
          <InfoItem label="ÿ±ÿ™ÿ®Ÿá ÿπŸÑŸÖ€å" value={teacher.academicRank} />
          <InfoItem label="ÿ™ÿßÿ±€åÿÆ ÿßÿÆÿ∞ ÿ±ÿ™ÿ®Ÿá" value={formatDate(teacher.rankAchievementDate)} />
          <InfoItem label="ÿ™ÿßÿ±€åÿÆ ÿ™ŸÇÿ±ÿ±€å ŸÖÿ±ÿ®€å" value={formatDate(teacher.trainerAppointmentDate)} />
          <InfoItem label="ÿ¨ŸÜÿ≥€åÿ™" value={teacher.gender} />
          <InfoItem label="ŸàŸÑÿß€åÿ™" value={teacher.province} />
          <InfoItem label="ŸÖÿ∂ŸÖŸàŸÜ" value={teacher.subject} />
          <InfoItem label="Ÿàÿ∏€åŸÅŸá / ŸÖŸàŸÇŸÅ" value={teacher.position} />
          <InfoItem label="ÿ¥ŸÅÿßÿÆÿßŸÜŸá" value={teacher.hospital} />
          <InfoItem label="ÿ™ÿßÿ±€åÿÆ ÿ™ŸàŸÑÿØ" value={formatDate(teacher.dateOfBirth)} />
          <InfoItem label="ŸÜŸÖÿ®ÿ± ÿ™ÿ∞⁄©ÿ±Ÿá" value={teacher.idNumber} />
          <InfoItem label="ÿ™ÿßÿ±€åÿÆ ÿ¥ÿ±Ÿàÿπ Ÿàÿ∏€åŸÅŸá" value={formatDate(teacher.dutyStartDate)} />
          <InfoItem label="ÿ¥ŸÖÿßÿ±Ÿá ÿ™ŸÖÿßÿ≥" value={teacher.contactInfo} />
          <InfoItem label="ŸÜŸÖÿ®ÿ± Ÿàÿßÿ™ÿ≥ÿßŸæ" value={teacher.whatsappNumber} />
          <InfoItem label="ÿß€åŸÖ€åŸÑ ÿ¢ÿØÿ±ÿ≥" value={teacher.emailAddress} />
          <InfoItem label="⁄©ŸàÿØ ŸæŸàÿ≥ÿ™" value={teacher.postCode} />
          <InfoItem label="ŸÜŸàÿπ ÿ™ŸÇÿ±ÿ±€å" value={teacher.appointmentType} />
          {/* <InfoItem label="ÿØ€åŸæÿßÿ±ÿ™ŸÖŸÜÿ™" value={teacher.department} /> */}
          {/* <InfoItem label="ÿ™ÿ¨ÿ±ÿ®Ÿá ⁄©ÿßÿ±€å" value={`${teacher.experience} ÿ≥ÿßŸÑ`} /> */}
          <InfoItem
            label="Ÿàÿ∂ÿπ€åÿ™"
            value={
              <Badge
                className={
                  teacher.status === "active"
                    ? "bg-green-100 text-green-800 px-3 py-1 rounded-full"
                    : "bg-red-100 text-red-800 px-3 py-1 rounded-full"
                }
              >
                {teacher.status === "active" ? "ÿ®ÿ±ÿ≠ÿßŸÑ" : "ŸÖŸÜŸÅ⁄©"}
              </Badge>
            }
          />
        </div>

        {/* ÿØ⁄©ŸÖŸá‚ÄåŸáÿß */}
        <div className="mt-8 flex justify-end gap-4">
          <Button
            onClick={handlePrint}
            className="bg-indigo-600 hover:bg-indigo-700 text-white font-semibold px-6 py-2 rounded-xl shadow-md transition-all"
          >
            Ÿæÿ±€åŸÜÿ™ / PDF
          </Button>
          <Button
            onClick={onClose}
            variant="outline"
            className="bg-red-600 hover:bg-red-700 text-white font-semibold px-6 py-2 rounded-xl shadow-md transition-all"
          >
            ÿ®ÿ≥ÿ™ŸÜ
          </Button>
        </div>
      </DialogContent>
    </Dialog>
  );
}

function InfoItem({ label, value }: { label: string; value: React.ReactNode }) {
  return (
    <div className="bg-slate-50 dark:bg-slate-800 p-4 rounded-xl shadow-sm">
      <p className="text-gray-500 text-xs mb-1">{label}</p>
      <p className="font-medium text-slate-900 dark:text-slate-100">{value || "ŸÜÿßŸÖÿ¥ÿÆÿµ"}</p>
    </div>
  );
}

client/src/hooks/useAuth.ts.content:
import { useQuery } from "@tanstack/react-query";
import type { User } from "@shared/schema";
import { getQueryFn } from "@/lib/queryClient";

export function useAuth() {
  const { data: user, isLoading, error } = useQuery<User>({
    queryKey: ["/api/auth/user"],
    queryFn: getQueryFn({ on401: "returnNull" }),
    retry: false,
  });

  return {
    user,
    isLoading,
    isAuthenticated: !!user && !error,
  };
}

