import React, { useState } from "react";
import { useTrainer } from "@/context/TrainerContext";

interface MonographEvaluation {
  section: string;
  percentage: string;
  score: string;
  teacherName: string;
  teacherSigned: boolean;
  characteristics: string;
  total: string;
  average: string;
  notes: string;
}

export default function MonographEvaluationForm() {
  const { trainerId } = useTrainer();
  const [name, setName] = useState("");
  const [lastName, setLastName] = useState("");
  const [fatherName, setFatherName] = useState("");
  const [idNumber, setIdNumber] = useState("");
  const [field, setField] = useState("");
  const [trainingYear, setTrainingYear] = useState("");
  const [startYear, setStartYear] = useState("");
  const [date, setDate] = useState("");

  const inputClass = "border px-2 py-1 text-center w-full";

  const [evaluations, setEvaluations] = useState<MonographEvaluation[]>([
    { section: "ÿ¥€åŸàŸá ÿ™ÿ≠ÿ±€åÿ± Ÿà ÿ™ÿ±ÿ™€åÿ® ŸÖŸàŸÜŸà⁄Øÿ±ÿßŸÅ", percentage: "", score: "", teacherName: "", teacherSigned: false, characteristics: "", total: "", average: "", notes: "" },
    { section: "ÿ≠ÿß⁄©ŸÖ€åÿ™ Ÿà ÿ¥€åŸàŸá ÿßÿ±ÿßÿ¶Ÿá ŸÖŸàÿ∂Ÿàÿπ", percentage: "", score: "", teacherName: "", teacherSigned: false, characteristics: "", total: "", average: "", notes: "" },
    { section: "ÿßÿ±ÿßÿ¶Ÿá ÿ¨Ÿàÿßÿ® ÿ®Ÿá ÿ≥ŸàÿßŸÑÿßÿ™ ÿ±ÿßÿ¨ÿπ ÿ®Ÿá ŸÖŸàÿ∂Ÿàÿπ", percentage: "", score: "", teacherName: "", teacherSigned: false, characteristics: "", total: "", average: "", notes: "" },
    { section: "ÿØŸÅÿßÿπ ÿßÿ≤ ŸÖŸàÿ∂Ÿàÿπ ÿ™ÿ≠ŸÇ€åŸÇ", percentage: "", score: "", teacherName: "", teacherSigned: false, characteristics: "", total: "", average: "", notes: "" },
    { section: "ÿßÿ±ÿßÿ¶Ÿá ÿ¨Ÿàÿßÿ®ÿßÿ™ ÿ®Ÿá ÿ≥ŸàÿßŸÑÿßÿ™ ÿßŸÅÿßŸÇ€å", percentage: "", score: "", teacherName: "", teacherSigned: false, characteristics: "", total: "", average: "", notes: "" },
    { section: "⁄©ÿ±⁄©ÿ™ÿ±ÿ≥ÿ™€å⁄©", percentage: "", score: "", teacherName: "", teacherSigned: false, characteristics: "", total: "", average: "", notes: "" }
  ]);

  const handleEvalChange = (index: number, field: keyof MonographEvaluation, value: string | boolean) => {
    const updated = [...evaluations];
    (updated[index] as any)[field] = value;
    setEvaluations(updated);
  };

  const handleSubmit = async () => {
    if (!trainerId) {
      alert("Trainer ID ÿÆÿßŸÑ€å ÿßÿ≥ÿ™.");
      return;
    }

    const payload = {
      trainer: trainerId,
      name,
      lastName,
      fatherName,
      idNumber,
      field,
      trainingYear,
      startYear,
      date,
      evaluations,
    };

    try {
      const res = await fetch("http://localhost:5000/api/monographEvaluation", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });

      if (!res.ok) {
        const text = await res.text();
        console.error("Server error:", text);
        alert("ÿÆÿ∑ÿß ÿØÿ± ÿ∞ÿÆ€åÿ±Ÿá ŸÅÿ±ŸÖÿå ⁄©ŸÜÿ≥ŸàŸÑ ÿ±ÿß ÿ®ÿ±ÿ±ÿ≥€å ⁄©ŸÜ€åÿØ");
        return;
      }

      alert("ŸÅÿ±ŸÖ ÿ®ÿß ŸÖŸàŸÅŸÇ€åÿ™ ÿ∞ÿÆ€åÿ±Ÿá ÿ¥ÿØ!");
      // ÿ±€åÿ≥ÿ™ ŸÅÿ±ŸÖ
      setName(""); setLastName(""); setFatherName(""); setIdNumber(""); setField(""); setTrainingYear(""); setStartYear(""); setDate("");
      setEvaluations([
        { section: "ÿ¥€åŸàŸá ÿ™ÿ≠ÿ±€åÿ± Ÿà ÿ™ÿ±ÿ™€åÿ® ŸÖŸàŸÜŸà⁄Øÿ±ÿßŸÅ", percentage: "", score: "", teacherName: "", teacherSigned: false, characteristics: "", total: "", average: "", notes: "" },
        { section: "ÿ≠ÿß⁄©ŸÖ€åÿ™ Ÿà ÿ¥€åŸàŸá ÿßÿ±ÿßÿ¶Ÿá ŸÖŸàÿ∂Ÿàÿπ", percentage: "", score: "", teacherName: "", teacherSigned: false, characteristics: "", total: "", average: "", notes: "" },
        { section: "ÿßÿ±ÿßÿ¶Ÿá ÿ¨Ÿàÿßÿ® ÿ®Ÿá ÿ≥ŸàÿßŸÑÿßÿ™ ÿ±ÿßÿ¨ÿπ ÿ®Ÿá ŸÖŸàÿ∂Ÿàÿπ", percentage: "", score: "", teacherName: "", teacherSigned: false, characteristics: "", total: "", average: "", notes: "" },
        { section: "ÿØŸÅÿßÿπ ÿßÿ≤ ŸÖŸàÿ∂Ÿàÿπ ÿ™ÿ≠ŸÇ€åŸÇ", percentage: "", score: "", teacherName: "", teacherSigned: false, characteristics: "", total: "", average: "", notes: "" },
        { section: "ÿßÿ±ÿßÿ¶Ÿá ÿ¨Ÿàÿßÿ®ÿßÿ™ ÿ®Ÿá ÿ≥ŸàÿßŸÑÿßÿ™ ÿßŸÅÿßŸÇ€å", percentage: "", score: "", teacherName: "", teacherSigned: false, characteristics: "", total: "", average: "", notes: "" },
        { section: "⁄©ÿ±⁄©ÿ™ÿ±ÿ≥ÿ™€å⁄©", percentage: "", score: "", teacherName: "", teacherSigned: false, characteristics: "", total: "", average: "", notes: "" }
      ]);
    } catch (err) {
      console.error(err);
      alert("ÿÆÿ∑ÿß ÿØÿ± ÿßÿ±ÿ™ÿ®ÿßÿ∑ ÿ®ÿß ÿ≥ÿ±Ÿàÿ±");
    }
  };

  return (
    <div className="max-w-6xl mx-auto bg-white shadow-lg rounded-2xl p-6 mt-6">
      <h2 className="text-xl font-bold text-center mb-4">ŸÅÿ±ŸÖ ÿßÿ±ÿ≤€åÿßÿ®€å ŸÖŸàŸÜŸà⁄Øÿ±ÿßŸÅ</h2>

      {/* ŸÖÿ¥ÿÆÿµÿßÿ™ ŸÅÿ±ÿØ€å */}
      <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-6">
        <input type="text" placeholder="ÿßÿ≥ŸÖ" value={name} onChange={(e)=>setName(e.target.value)} className={inputClass}/>
        <input type="text" placeholder="ÿ™ÿÆŸÑÿµ" value={lastName} onChange={(e)=>setLastName(e.target.value)} className={inputClass}/>
        <input type="text" placeholder="ŸàŸÑÿØ" value={fatherName} onChange={(e)=>setFatherName(e.target.value)} className={inputClass}/>
        <input type="text" placeholder="ŸÜŸÖÿ®ÿ± ÿ™ÿ∞⁄©ÿ±Ÿá" value={idNumber} onChange={(e)=>setIdNumber(e.target.value)} className={inputClass}/>
        <input type="text" placeholder="ÿ±ÿ¥ÿ™Ÿá" value={field} onChange={(e)=>setField(e.target.value)} className={inputClass}/>
        <input type="text" placeholder="ÿ≥ÿßŸÑ ÿ™ÿ±€åŸÜŸÜ⁄Ø" value={trainingYear} onChange={(e)=>setTrainingYear(e.target.value)} className={inputClass}/>
        <input type="text" placeholder="ÿ≥ÿßŸÑ ÿ¥ŸÖŸàŸÑ" value={startYear} onChange={(e)=>setStartYear(e.target.value)} className={inputClass}/>
        <input type="text" placeholder="ÿ™ÿßÿ±€åÿÆ" value={date} onChange={(e)=>setDate(e.target.value)} className={inputClass}/>
      </div>

      {/* ÿ¨ÿØŸàŸÑ ÿßÿ±ÿ≤€åÿßÿ®€å */}
      <table className="table-auto border-collapse border w-full text-center mb-4">
        <thead>
          <tr className="bg-gray-100">
            <th className="border px-2 py-2">ÿ®ÿÆÿ¥‚ÄåŸáÿß</th>
            <th className="border px-2 py-2">ŸÅ€åÿµÿØ€å</th>
            <th className="border px-2 py-2">ŸÜŸÖÿ±Ÿá ÿØÿßÿØŸá ÿ¥ÿØŸá</th>
            <th className="border px-2 py-2">ÿßÿ≥ŸÖ ÿßÿ≥ÿ™ÿßÿØ</th>
          </tr>
        </thead>
        <tbody>
          {evaluations.map((evalItem, idx)=>(
            <tr key={idx}>
              <td className="border px-2 py-2">{evalItem.section}</td>
              <td className="border px-2 py-2"><input className={inputClass} value={evalItem.percentage} onChange={e=>handleEvalChange(idx, "percentage", e.target.value)} /></td>
              <td className="border px-2 py-2"><input className={inputClass} value={evalItem.score} onChange={e=>handleEvalChange(idx, "score", e.target.value)} /></td>
              <td className="border px-2 py-2"><input className={inputClass} value={evalItem.teacherName} onChange={e=>handleEvalChange(idx, "teacherName", e.target.value)} /></td>
            </tr>
          ))}
          <tr>
            <td className="border px-2 py-2">ŸÖÿ¨ŸÖŸàÿπ ŸÜŸÖÿ±ÿßÿ™</td>
            <td className="border px-2 py-2"><input className={inputClass} value={evaluations[0].total} onChange={e=>handleEvalChange(0, "total", e.target.value)} /></td>
            <td className="border px-2 py-2">ÿßŸàÿ≥ÿ∑</td>
            <td className="border px-2 py-2"><input className={inputClass} value={evaluations[0].average} onChange={e=>handleEvalChange(0, "average", e.target.value)} /></td>
          </tr>
        </tbody>
      </table>

      <div className="mb-4">
        <label className="block mb-1 font-medium">ŸÖŸÑÿßÿ≠ÿ∏ÿßÿ™ / ŸÜÿ∏ÿ± Ÿá€åÿ¶ÿ™ ÿßÿØÿßÿ±€å</label>
        <textarea className="border w-full p-2 rounded" rows={4} value={evaluations[0].notes} onChange={e=>handleEvalChange(0, "notes", e.target.value)} />
      </div>

      <div className="text-center">
        <button onClick={handleSubmit} className="px-6 py-2 bg-green-600 text-white rounded-xl hover:bg-green-700 transition">
          ÿ∞ÿÆ€åÿ±Ÿá ŸÅÿ±ŸÖ
        </button>
      </div>
    </div>
  );
}

