  percent: number;
  months?: MonthScore[];
}

interface Section {
  name: string;
  percent: number;
  activities: Activity[];
}

interface FormFDataFromAPI {
  studentName: string;
  fatherName: string;
  year: string;
  scores: Record<string, Record<number, number>>;
}

interface FormFViewDynamicProps {
  studentName: string;
}

const sectionsTemplate: Section[] = [
  {
