      { id: "patients", title: "ÿ®ÿ±ÿÆŸàÿ±ÿØ ÿ®ÿß ŸÖÿ±€åÿ∂", percent: 2 },
    ],
  },
  {
    name: "ÿ¥€åŸàŸá ÿßÿÆÿ∞ ŸÖÿ¥ÿßŸáÿØŸá",
    percent: 9,
    activities: [
      { id: "cc", title: "ÿ¥Ÿáÿ±ÿ™ ŸÖÿ±€åÿ∂", percent: 2 },
      { id: "pi", title: "ŸÖÿπÿß€åŸÜŸá ŸÅÿ≤€å⁄©€å", percent: 2 },
      { id: "postHistory", title: "ÿ™ÿ¨Ÿà€åÿ≤ ŸÖÿπÿß€åŸÜÿßÿ™ ŸÑÿßÿ®ÿ±ÿßÿ™Ÿàÿßÿ±€å ÿ±Ÿàÿ™€åŸÜ", percent: 2 },
      { id: "diagnosis", title: "ÿ™ÿ¨Ÿà€åÿ≤ ŸÖÿπÿß€åŸÜÿßÿ™ ŸàÿµŸÅ€å Ÿà ÿ∂ŸÖ€åŸÖŸà€å", percent: 3 },
    ],
  },
  {
    name: "ÿßŸÜÿ¨ÿßŸÖ ŸÖÿ¥Ÿàÿ±Ÿá ÿ∑ÿ®€å ÿ®ŸÖŸàŸÇÿπ",
    percent: 6,
    activities: [
      { id: "consult", title: "ÿßŸÜÿ¨ÿßŸÖ ŸÖÿ¥Ÿàÿ±Ÿá ÿ∑ÿ®€å ÿ®ŸÖŸàŸÇÿπ", percent: 6 }
    ],
  },
  {
    name: "ÿ≥ÿπ€å ÿØÿ± ÿ®ŸÑŸÜÿØ ÿ®ÿ±ÿØŸÜ ÿ≥ÿ∑ÿ≠ ÿØÿßŸÜÿ¥ ÿπŸÑŸÖ€å Ÿà ŸÖÿ≥ŸÑ⁄©€å",
    percent: 27,
    activities: [
      { id: "morning", title: "ÿßÿ¥ÿ™ÿ±ÿß⁄© ŸÅÿπÿßŸÑ ÿØÿ± ÿ±ÿßŸæŸà ÿµÿ®ÿ≠ÿßŸÜŸá", percent: 6 },
      { id: "visits", title: "ÿßÿ¥ÿ™ÿ±ÿß⁄© ŸÅÿπÿßŸÑ ÿØÿ± Ÿà€åÿ≤ÿ™‚ÄåŸáÿß", percent: 6 },
      { id: "conferences", title: "ÿßÿ¥ÿ™ÿ±ÿß⁄© ŸÅÿπÿßŸÑ ÿØÿ± ⁄©ŸÜŸÅÿ±ÿßŸÜÿ≥‚ÄåŸáÿß", percent: 12 },
      { id: "license", title: "ÿ™ŸÇŸà€åŸá €å⁄©€å ÿßÿ≤ ŸÑ€åÿ≥ÿßŸÜÿ≥‚ÄåŸáÿß€å ŸÖÿπÿ™ÿ®ÿ± ÿÆÿßÿ±ÿ¨€å", percent: 1 },
      { id: "computer", title: "ŸÇÿØÿ±ÿ™ ÿßÿ≥ÿ™ŸÅÿßÿØŸá ÿßÿ≤ ⁄©ŸÖŸæ€åŸàÿ™ÿ± Ÿà ÿßŸÜÿ™ÿ±ŸÜÿ™", percent: 1 },
      { id: "press", title: "ÿßÿ≥ÿ™ŸÅÿßÿØŸá ÿßÿ≤ ŸÜÿ¥ÿ±ÿßÿ™ ŸÖÿ∑ÿ®Ÿàÿπ", percent: 1 },
    ],
  },
  {
    name: "ÿØÿ≥ŸæŸÑ€åŸÜ",
    percent: 24,
    activities: [
      { id: "attendance", title: "ÿ≠ÿßÿ∂ÿ± ÿ®ŸàÿØŸÜ", percent: 6 },
      { id: "obedience", title: "ÿßÿ∑ÿßÿπÿ™ ÿßÿ≤ ÿßŸàÿßŸÖÿ± ŸÖÿπŸÇŸàŸÑ ÿ¢ŸÖÿ±ŸÖÿßŸÅŸàŸÇ", percent: 6 },
      { id: "rules", title: "ŸÖÿ±ÿßÿπÿßÿ™ ŸÖŸÇÿ±ÿ±Ÿá Ÿà ŸÑŸàÿß€åÿ≠ ÿ™ÿ±€åŸÜŸÜ⁄Ø", percent: 6 },
      { id: "duty", title: "ÿßÿ¥ÿ™ÿ±ÿß⁄© ÿØÿ± ŸÜŸà⁄©ÿ±€åŸàÿßŸÑ€å", percent: 6 },
    ],
  },
  {
    name: "ÿÆÿµŸàÿµ€åÿßÿ™ ŸÅÿ±ÿØ€å",
    percent: 24,
    activities: [
      { id: "expression", title: "ÿßŸÅÿßÿØŸá ÿ®€åÿßŸÜ", percent: 2 },
      { id: "initiative", title: "ÿßÿ®ÿ™⁄©ÿßÿ± ÿ≥ÿßŸÑŸÖ", percent: 2 },
      { id: "leadership", title: "ÿ™ÿµŸÖ€åŸÖ Ÿà ÿ±Ÿáÿ®ÿ±€å", percent: 2 },
      { id: "honesty", title: "ÿ±ÿßÿ≥ÿ™⁄©ÿßÿ±€å Ÿà ŸáŸÖ⁄©ÿßÿ±€å", percent: 2 },
      { id: "resources", title: "ÿßÿ≥ÿ™ŸÅÿßÿØŸá ŸÖÿπŸÇŸàŸÑ ÿßÿ≤ ŸÖŸÜÿßÿ®ÿπ", percent: 4 },
      { id: "responsibility", title: "ŸÖÿ≥ŸîŸàŸÑ€åÿ™‚ÄåŸæÿ∞€åÿ±€å", percent: 2 },
      { id: "evaluation", title: "ÿ™ÿ≠ŸÑ€åŸÑ Ÿà ÿßÿ±ÿ≤€åÿßÿ®€å", percent: 2 },
      { id: "feedback", title: "ÿßŸÜÿ™ŸÇÿßÿØ Ÿà Ÿæ€åÿ¥ŸÜŸáÿßÿØ ÿ≥ÿßÿ≤ŸÜÿØŸá", percent: 2 },
      { id: "individual", title: "ÿ±ÿ≥€åÿØ⁄Ø€å ÿ®Ÿá Ÿàÿ∂ÿπ ŸÅÿ±ÿØ€å", percent: 2 },
      { id: "social", title: "ÿ±ÿßÿ®ÿ∑Ÿá ÿßÿ¨ÿ™ŸÖÿßÿπ€å", percent: 2 },
      { id: "position", title: "ÿßÿ≥ÿ™ŸÅÿßÿØŸá ÿ®ÿ¨ÿß ÿßÿ≤ ŸÖŸàŸÇŸÅ ⁄©ÿßÿ±€å", percent: 2 },
    ],
  },
];

// ÿ™ÿßÿ®ÿπ ⁄©ŸÖ⁄©€å ÿ®ÿ±ÿß€å ŸÜŸÖÿß€åÿ¥ ŸÖŸÇÿØÿßÿ± - ÿ¨ÿß€å⁄Øÿ≤€åŸÜ€å ÿµŸÅÿ± ÿ®ÿß ÿÆÿßŸÜŸá ÿÆÿßŸÑ€å
const displayValue = (value: number | null): string => {
  return value === null || value === 0 ? "" : value.toString();
};

// ÿ™ÿßÿ®ÿπ ⁄©ŸÖ⁄©€å ÿ®ÿ±ÿß€å ŸÖÿ≠ÿßÿ≥ÿ®Ÿá ÿ¨ŸÖÿπ - ŸÅŸÇÿ∑ ŸÖŸÇÿßÿØ€åÿ± ÿ∫€åÿ±ÿµŸÅÿ± Ÿà ÿ∫€åÿ±ŸÜÿßŸÑ
const calculateSum = (values: (number | null)[]): number => {
  return values.reduce((sum, val) => sum + (val || 0), 0);
};

export default function FormFViewDynamicFinal({ studentName }: FormFViewDynamicProps) {
  const [sections, setSections] = useState<Section[]>(sectionsTemplate);
  const [studentInfo, setStudentInfo] = useState({
    studentName: "",
    fatherName: "",
    year: "",
  });

  useEffect(() => {
    const fetchData = async () => {
      try {
        if (!studentName) return;

        const res = await axios.get<FormFDataFromAPI>(
          `/api/checklists/student/${encodeURIComponent(studentName)}`
        );

        const data = res.data;

        setStudentInfo({
          studentName: data.studentName,
          fatherName: data.fatherName,
          year: data.year,
        });

        const updatedSections = sectionsTemplate.map((section) => ({
          ...section,
          activities: section.activities.map((act) => ({
            ...act,
            months: months.map((m) => ({
              month: m,
              value: data.scores?.[act.id]?.[m] ?? null,
            })),
          })),
        }));

        setSections(updatedSections);
      } catch (err) {
        console.error("ÿÆÿ∑ÿß ÿØÿ± ÿØÿ±€åÿßŸÅÿ™ ÿØÿßÿØŸá‚ÄåŸáÿß:", err);
      }
    };

    fetchData();
  }, [studentName]);

  const calculateActivityTotal = (activity: Activity) =>
    calculateSum((activity.months || []).map(m => m.value));

  const calculateSectionTotal = (section: Section) =>
    calculateSum(section.activities.map(act => calculateActivityTotal(act)));

  const calculateMonthTotal = (monthIndex: number) =>
    calculateSum(sections.map(section => 
      calculateSum(section.activities.map(act => 
        act.months?.[monthIndex]?.value || 0
      ))
    ));

  const calculateGrandTotal = () =>
    calculateSum(sections.map(section => calculateSectionTotal(section)));

  const downloadPDF = () => {
    const printWindow = window.open('', '_blank');
    if (!printWindow) return;

    const monthTotals = months.map((_, monthIndex) => 
      calculateMonthTotal(monthIndex)
    );

    const htmlContent = `
      <!DOCTYPE html>
      <html dir="rtl" lang="fa">
      <head>
        <meta charset="UTF-8">
        <title>Form F - ${studentInfo.studentName}</title>
        <style>
          body { 
            font-family: 'Tahoma', 'Arial', sans-serif; 
            margin: 10px;
            line-height: 1;
            font-size: 11px;
          }
          .header { 
            text-align: center; 
            margin-bottom: 10px;
          }
          .header h1 {
            font-size: 14px;
            margin: 0;
            font-weight: bold;
          }
          .header h2 {
            font-size: 12px;
            margin: 2px 0;
            font-weight: bold;
          }
          .student-info {
            text-align: center;
            margin-bottom: 10px;
            font-size: 11px;
          }
          table {
            width: 100%;
            border-collapse: collapse;
            margin-bottom: 5px;
            font-size: 9px;
            border: 1px solid #000;
          }
          th, td {
            border: 1px solid #000;
            padding: 3px 2px;
            text-align: center;
            height: 18px;
          }
          .main-section {
            background-color: #f0f0f0;
            font-weight: bold;
          }
          .sub-activity {
            background-color: #ffffff;
          }
          .empty-cell {
            border: none;
