          <span><strong>ÿ≥ÿßŸÑ ÿ™ÿ±ÿ®ŸÜŸÜ⁄Ø:</strong> ${studentInfo.year}</span>
        </div>

        <table>
          <thead>
            <tr>
              <th>ŸÖÿ≠ŸÅÿ∏ÿßÿ™</th>
              <th>ÿ¨ŸÖÿπ‚ÄåŸáÿß ÿ®ÿ±ÿ¢ŸÜ</th>
              ${months.map(m => `<th>${m}</th>`).join('')}
              <th>ŸÇ€åŸÖÿ™‚ÄåŸáÿß ÿ®ÿ±ÿ¢ŸÜ</th>
            </tr>
          </thead>
          <tbody>
            ${sections.map(section => `
              <!-- ÿ®ÿÆÿ¥ ÿßÿµŸÑ€å -->
              <tr class="main-section">
                <td>${section.percent}%</td>
                <td>${calculateSectionTotal(section)}</td>
                ${months.map((_, idx) => {
                  const monthSum = section.activities.reduce((sum, act) => {
                    const value = act.months?.[idx]?.value;
                    return sum + (value || 0);
                  }, 0);
                  return `<td>${monthSum > 0 ? monthSum : ''}</td>`;
                }).join('')}
                <td>${section.name}</td>
              </tr>
              <!-- ŸÅÿπÿßŸÑ€åÿ™‚ÄåŸáÿß€å ŸÅÿ±ÿπ€å -->
              ${section.activities.map((activity, index) => `
                <tr class="sub-activity">
                  <td class="empty-cell"></td>
                  <td>${calculateActivityTotal(activity) > 0 ? calculateActivityTotal(activity) : ''}</td>
                  ${activity.months?.map(month => 
                    `<td>${displayValue(month.value)}</td>`
                  ).join('')}
                  <td>${activity.title} ${activity.percent}%</td>
                </tr>
              `).join('')}
            `).join('')}

            <!-- ÿ¨ŸÖÿπ ⁄©ŸÑ -->
            <tr class="main-section" style="background-color: #e0e0e0;">
              <td>100%</td>
              <td>${calculateGrandTotal()}</td>
              ${monthTotals.map(total => 
                `<td>${total > 0 ? total : ''}</td>`
              ).join('')}
              <td>ŸÖÿ¨ŸÖŸàÿπ ⁄©ŸÑ</td>
            </tr>
          </tbody>
        </table>

        <div class="signatures">
          <div class="signature-box">
            <strong>ÿ±ÿ¶Ÿäÿ≥ ÿ¥ŸÜÿßÿÆÿ™Ÿá</strong><br>
            ________________
          </div>
          <div class="signature-box">
            <strong>ÿßŸÖÿ±Ÿäÿ±Ÿà⁄Øÿ±ÿßŸÖ ÿ™ÿ±ÿ®ŸÜŸÜ⁄Ø</strong><br>
            ________________
          </div>
        </div>

        <div style="text-align: center; margin-top: 5px; font-size: 10px;">
          <strong>ÿ™ÿ±ŸäŸÜÿ≤ŸÖÿ±ÿ®Ÿàÿ∑Ÿá</strong>
        </div>

        <script>
          window.onload = function() {
            window.print();
          }
        </script>
      </body>
      </html>
    `;

    printWindow.document.write(htmlContent);
    printWindow.document.close();
  };

  const downloadExcel = () => {
    let csvContent = "data:text/csv;charset=utf-8,\uFEFF";
    
    csvContent += "( F ) ŸÅŸàÿ±ŸÖ:\\n";
    csvContent += "ÿ≠⁄©ÿßŸÑÿ≥ÿ™ ⁄©ÿßÿ±€å Ÿà ÿßÿ±ÿ≤€åÿßÿ®€å ŸÖÿßŸÖŸàÿßŸÑ ŸÜÿ≤€åŸÜ€å Ÿáÿß€å ÿ≥ÿßÿÆÿßŸÜŸá\\n";
    csvContent += `ÿßÿ≥ŸÖ: ${studentInfo.studentName},ŸàŸÑÿØ: ${studentInfo.fatherName},ÿ≥ÿßŸÑ ÿ™ÿ±ÿ®ŸÜŸÜ⁄Ø: ${studentInfo.year}\\n\\n`;

    csvContent += "ŸÖÿ≠ŸÅÿ∏ÿßÿ™,ÿ¨ŸÖÿπ‚ÄåŸáÿß ÿ®ÿ±ÿ¢ŸÜ,";
    csvContent += months.join(",");
    csvContent += ",ŸÇ€åŸÖÿ™‚ÄåŸáÿß ÿ®ÿ±ÿ¢ŸÜ\\n";

    sections.forEach(section => {
      // ÿ®ÿÆÿ¥ ÿßÿµŸÑ€å
      let sectionRow = `${section.percent}%,${calculateSectionTotal(section)},`;
      months.forEach((_, idx) => {
        const monthSum = section.activities.reduce((sum, act) => {
          const value = act.months?.[idx]?.value;
          return sum + (value || 0);
        }, 0);
        sectionRow += `${monthSum > 0 ? monthSum : ''},`;
      });
      sectionRow += `${section.name}\\n`;
      csvContent += sectionRow;

      // ŸÅÿπÿßŸÑ€åÿ™‚ÄåŸáÿß€å ŸÅÿ±ÿπ€å
      section.activities.forEach(activity => {
        let activityRow = `,${calculateActivityTotal(activity) > 0 ? calculateActivityTotal(activity) : ''},`;
        activity.months?.forEach(month => {
          activityRow += `${displayValue(month.value)},`;
        });
        activityRow += `${activity.title} ${activity.percent}%\\n`;
        csvContent += activityRow;
      });
    });

    // ÿ¨ŸÖÿπ ⁄©ŸÑ
    let grandTotalRow = "100%,";
    grandTotalRow += `${calculateGrandTotal()},`;
    months.forEach((_, idx) => {
      const monthTotal = calculateMonthTotal(idx);
      grandTotalRow += `${monthTotal > 0 ? monthTotal : ''},`;
    });
    grandTotalRow += "ŸÖÿ¨ŸÖŸàÿπ ⁄©ŸÑ\\n";
    csvContent += grandTotalRow;

    csvContent += "\\nÿ±ÿ¶Ÿäÿ≥ ÿ¥ŸÜÿßÿÆÿ™Ÿá,ÿßŸÖÿ±Ÿäÿ±Ÿà⁄Øÿ±ÿßŸÖ ÿ™ÿ±ÿ®ŸÜŸÜ⁄Ø\\n";
    csvContent += "ÿ™ÿ±ŸäŸÜÿ≤ŸÖÿ±ÿ®Ÿàÿ∑Ÿá\\n";

    const encodedUri = encodeURI(csvContent);
    const link = document.createElement("a");
    link.setAttribute("href", encodedUri);
    link.setAttribute("download", `FormF_${studentInfo.studentName}.csv`);
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
  };

  return (
    <div className="max-w-full mx-auto p-6 bg-gray-100 rounded-xl shadow-md">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-2xl font-bold text-center">
          ŸÜŸÖÿß€åÿ¥ ⁄Ü⁄© ŸÑ€åÿ≥ÿ™ ⁄©ÿßÿ±€å Ÿà ÿßÿ±ÿ≤€åÿßÿ®€å ŸÖÿßŸáŸàÿßÿ± ÿ™ÿ±€åŸÜ€å‚ÄåŸáÿß (Form F)
        </h1>
        
        <div className="flex gap-2 no-print">
          <button
            onClick={downloadPDF}
            className="bg-red-600 hover:bg-red-700 text-white px-4 py-2 rounded-lg font-bold"
          >
            ÿØÿßŸÜŸÑŸàÿØ PDF
          </button>
          <button
            onClick={downloadExcel}
            className="bg-green-600 hover:bg-green-700 text-white px-4 py-2 rounded-lg font-bold"
          >
            ÿØÿßŸÜŸÑŸàÿØ Excel
          </button>
        </div>
      </div>

      <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
        <div className="p-3 border rounded-lg bg-white">
          <strong>ŸÜÿßŸÖ ÿ™ÿ±€åŸÜ€å:</strong> {studentInfo.studentName}
        </div>
        <div className="p-3 border rounded-lg bg-white">
          <strong>ŸàŸÑÿØ:</strong> {studentInfo.fatherName}
        </div>
        <div className="p-3 border rounded-lg bg-white">
          <strong>ÿ≥ÿßŸÑ ÿ¢ŸÖŸàÿ≤ÿ¥€å:</strong> {studentInfo.year}
        </div>
      </div>

      {/* ŸÜŸÖÿß€åÿ¥ ÿ®ÿß ÿ≥ÿßÿÆÿ™ÿßÿ± ÿØÿ±ÿÆÿ™€å */}
      <div className="mb-8">
        <table className="w-full border border-gray-300 text-sm bg-white">
          <thead>
            <tr className="bg-gray-200">
              <th className="p-2 border border-gray-300">ÿ®ÿÆÿ¥ ÿßÿµŸÑ€å</th>
              <th className="p-2 border border-gray-300">ŸÅ€åÿµÿØ€å ÿ®ÿÆÿ¥</th>
              <th className="p-2 border border-gray-300">ŸÅÿπÿßŸÑ€åÿ™‚ÄåŸáÿß€å ŸÅÿ±ÿπ€å</th>
              <th className="p-2 border border-gray-300">ŸÅ€åÿµÿØ€å ŸÅÿπÿßŸÑ€åÿ™</th>
              {months.map((m) => (
                <th key={m} className="p-2 border border-gray-300">{m}</th>
              ))}
              <th className="p-2 border border-gray-300">ŸÖÿ¨ŸÖŸàÿπ ŸÅÿπÿßŸÑ€åÿ™</th>
            </tr>
          </thead>
          <tbody>
            {sections.map((section, sectionIndex) => (
              <React.Fragment key={sectionIndex}>
                <tr className="bg-gray-100 font-bold">
                  <td className="p-2 border border-gray-300" rowSpan={section.activities.length + 1}>
                    {section.name}
                  </td>
                  <td className="p-2 border border-gray-300" rowSpan={section.activities.length + 1}>
                    {section.percent}%
                  </td>
                  <td className="p-2 border border-gray-300" colSpan={months.length + 3}>
                    ÿ¨ŸÖÿπ ÿ®ÿÆÿ¥: {calculateSectionTotal(section) > 0 ? calculateSectionTotal(section) : ""}
                  </td>
                </tr>
                {section.activities.map((activity, activityIndex) => (
                  <tr key={activityIndex} className="hover:bg-gray-50">
                    <td className="p-2 border border-gray-300">{activity.title}</td>
                    <td className="p-2 border border-gray-300">{activity.percent}%</td>
                    {months.map((monthNum) => {
                      const monthData = activity.months?.find(m => m.month === monthNum);
                      return (
                        <td
                          key={monthNum}
                          className="p-2 border border-gray-300 text-center"
                        >
                          {displayValue(monthData?.value ?? null)}
                        </td>
                      );
                    })}
                    <td className="p-2 border border-gray-300 font-bold">
                      {calculateActivityTotal(activity) > 0 ? calculateActivityTotal(activity) : ""}
                    </td>
                  </tr>
                ))}
              </React.Fragment>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
}

