import React, { useState } from "react";
import { useTrainer } from "@/context/TrainerContext";

interface ConferenceRow {
  id: number;
  conferenceTitle: string;
  score: string;
  date: string;
  teacherName: string;
}

export default function EvaluationFormD() {
  const { trainerId } = useTrainer();
  const [year, setYear] = useState<string>("");
  const [name, setName] = useState<string>("");
  const [fatherName, setFatherName] = useState<string>("");
  const [department, setDepartment] = useState<string>("");
  const [trainingYear, setTrainingYear] = useState<string>("");

  const [rows, setRows] = useState<ConferenceRow[]>([
    { id: 1, conferenceTitle: "", score: "", date: "", teacherName: "" },
    { id: 2, conferenceTitle: "", score: "", date: "", teacherName: "" },
    { id: 3, conferenceTitle: "", score: "", date: "", teacherName: "" },
    { id: 4, conferenceTitle: "", score: "", date: "", teacherName: "" },
  ]);

  const inputClass = "border px-2 py-1 w-full text-center";

  const handleChangeRow = <K extends keyof ConferenceRow>(
    index: number,
    field: K,
    value: ConferenceRow[K]
  ) => {
    setRows(prev =>
      prev.map((row, i) =>
        i === index ? ({ ...row, [field]: value } as ConferenceRow) : row
      )
    );
  };

  const handleSubmit = async () => {
    if (!trainerId) {
      alert("‚ùå Trainer ID ÿÆÿßŸÑ€å ÿßÿ≥ÿ™ Ÿà ŸÜŸÖ€å‚Äåÿ™ŸàÿßŸÜ ŸÅÿ±ŸÖ ÿ±ÿß ÿ∞ÿÆ€åÿ±Ÿá ⁄©ÿ±ÿØ.");
      return;
    }

    const conferences = rows.map(({ conferenceTitle, score, date, teacherName }) => ({
      conferenceTitle,
      score,
      date,
      teacherName,
    }));

    const payload = { trainer: trainerId, year, name, fatherName, department, trainingYear, conferences };

    try {
      const res = await fetch("http://localhost:5000/api/conference", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(payload),
      });

      if (res.ok) {
        alert("‚úÖ ŸÅÿ±ŸÖ ÿ®ÿß ŸÖŸàŸÅŸÇ€åÿ™ ÿ∞ÿÆ€åÿ±Ÿá ÿ¥ÿØ");
           // ÿ±€åÿ≥ÿ™ ⁄©ÿ±ÿØŸÜ ŸÅ€åŸÑÿØŸáÿß Ÿà ÿ¨ÿØŸàŸÑ
      setYear("");
      setName("");
      setFatherName("");
      setDepartment("");
      setTrainingYear("");
      setRows([
        { id: 1, conferenceTitle: "", score: "", date: "", teacherName: "" },
        { id: 2, conferenceTitle: "", score: "", date: "", teacherName: "" },
        { id: 3, conferenceTitle: "", score: "", date: "", teacherName: "" },
        { id: 4, conferenceTitle: "", score: "", date: "", teacherName: "" },
      ]);
      } else {
        const data = await res.json();
        alert("‚ùå ÿÆÿ∑ÿß: " + (data.message || "Unknown error"));
        console.error("Server error:", data);
      }
    } catch (err) {
      console.error(err);
      alert("‚ùå ŸÖÿ¥⁄©ŸÑ ÿØÿ± ÿßÿ±ÿ™ÿ®ÿßÿ∑ ÿ®ÿß ÿ≥ÿ±Ÿàÿ±");
    }
  };

  return (
    <div className="max-w-6xl mx-auto bg-white shadow-lg rounded-2xl p-6 mt-6">
      <h2 className="text-xl font-bold text-center mb-4">ŸÅÿ±ŸÖ ÿßÿ±ÿ≤ÿ¥€åÿßÿ®€å ⁄©ŸÜŸÅÿ±ÿßŸÜÿ≥</h2>

      <div className="grid grid-cols-1 md:grid-cols-5 gap-4 mb-6">
        <div><label>ÿ≥ÿßŸÑ</label><input type="text" value={year} onChange={e => setYear(e.target.value)} className={inputClass} /></div>
        <div><label>ÿßÿ≥ŸÖ</label><input type="text" value={name} onChange={e => setName(e.target.value)} className={inputClass} /></div>
        <div><label>ŸàŸÑÿØ</label><input type="text" value={fatherName} onChange={e => setFatherName(e.target.value)} className={inputClass} /></div>
        <div><label>ÿØ€åŸæÿßÿ±ÿ™ŸÖŸÜÿ™</label><input type="text" value={department} onChange={e => setDepartment(e.target.value)} className={inputClass} /></div>
        <div><label>ÿ≥ÿßŸÑ ÿ™ÿ±€åŸÜŸÜ⁄Ø</label><input type="text" value={trainingYear} onChange={e => setTrainingYear(e.target.value)} className={inputClass} /></div>
