  static async createForm(req: Request, res: Response) {
    try {
      const formData = req.body;
      const newForm = new MonographEvaluationForm(formData);
      const savedForm = await newForm.save();
      
      res.status(201).json({
        success: true,
        message: 'ŸÅÿ±ŸÖ ÿ®ÿß ŸÖŸàŸÅŸÇ€åÿ™ ÿ∞ÿÆ€åÿ±Ÿá ÿ¥ÿØ',
        data: savedForm
      });
    } catch (error: any) {
      console.error('Error saving form:', error);
      res.status(500).json({
        success: false,
        message: 'ÿÆÿ∑ÿß ÿØÿ± ÿ∞ÿÆ€åÿ±Ÿá ŸÅÿ±ŸÖ'
      });
    }
  }

  // ÿØÿ±€åÿßŸÅÿ™ ÿ™ŸÖÿßŸÖ ŸÅÿ±ŸÖ‚ÄåŸáÿß
// controllers/monographController.ts
static async getAllForms(req: Request, res: Response) {
  try {
    // ŸÅÿπŸÑÿßŸã ŸáŸÖŸá‚Äå€å ŸÅÿ±ŸÖ‚ÄåŸáÿß ÿ±ÿß ŸÖ€å‚Äå⁄Ø€åÿ±€åŸÖ
