        body: JSON.stringify(payload),
      });

      if (res.ok) {
        alert("ŸÅÿ±ŸÖ ÿ®ÿß ŸÖŸàŸÅŸÇ€åÿ™ ÿßÿ±ÿ≥ÿßŸÑ ÿ¥ÿØ ‚úÖ");
        // ÿ±€åÿ≥ÿ™ ŸÅÿ±ŸÖ
        setName(""); setLastName(""); setFatherName(""); setIdNumber("");
        setField(""); setTrainingYear(""); setStartYear(""); setDate("");
        setChef(""); setDepartmentHead(""); setHospitalHead("");
        setEvaluations(
          sections.map((s) => ({
            section: s,
            percentage: "",
            score: "",
            teacherName: "",
          }))
        );
      } else alert("‚ùå ÿÆÿ∑ÿß ÿØÿ± ÿßÿ±ÿ≥ÿßŸÑ ŸÅÿ±ŸÖ");
