        <input className={inputClass} placeholder="ŸàŸÑÿØ" value={fatherName} onChange={(e) => setFatherName(e.target.value)} />
        <input className={inputClass} placeholder="ŸÜŸÖÿ®ÿ± ÿ™ÿ∞⁄©ÿ±Ÿá" value={idNumber} onChange={(e) => setIdNumber(e.target.value)} />

