        <input className={inputClass} placeholder="ÿ≥ÿßŸÑ ÿ™ÿ±€åŸÜŸÜ⁄Ø" value={trainingYear} onChange={(e) => setTrainingYear(e.target.value)} />
        <input className={inputClass} placeholder="ÿ≥ÿßŸÑ ÿ¥ŸÖŸàŸÑ" value={startYear} onChange={(e) => setStartYear(e.target.value)} />
        <input className={inputClass} placeholder="ÿ™ÿßÿ±€åÿÆ" value={date} onChange={(e) => setDate(e.target.value)} />

        {/* ÿßÿ±ÿ≤€åÿßÿ®€å ÿ®ÿÆÿ¥‚ÄåŸáÿß */}
        {evaluations.map((ev, i) => (
          <React.Fragment key={i}>
            <div className={inputClass}>{ev.section}</div>
            <input
              className={inputClass}
