            />
            <input
              className={inputClass}
              placeholder="ŸÜŸÖÿ±Ÿá ÿØÿßÿØŸá ÿ¥ÿØŸá"
              value={ev.score}
              onChange={(e) => handleEvalChange(i, "score", e.target.value)}
            />
            <input
              className={inputClass}
              placeholder="ŸÜÿßŸÖ ÿßÿ≥ÿ™ÿßÿØ"
              value={ev.teacherName}
              onChange={(e) => handleEvalChange(i, "teacherName", e.target.value)}
            />
          </React.Fragment>
        ))}

        {/* ŸÅ€åŸÑÿØŸáÿß€å ÿßÿ∂ÿßŸÅ€å */}
        <input className={inputClass} placeholder="ÿ¥ŸÅ" value={chef} onChange={(e) => setChef(e.target.value)} />
        <input className={inputClass} placeholder="ÿ¢ŸÖÿ± Ÿæÿ±Ÿà⁄Øÿ±ÿßŸÖ ÿ™ÿ±€åŸÜŸÜ⁄Ø" value={departmentHead} onChange={(e) => setDepartmentHead(e.target.value)} />
        <input className={inputClass} placeholder="ÿ±€åÿ≥ ÿ¥ŸÅÿßÿÆÿßŸÜŸá" value={hospitalHead} onChange={(e) => setHospitalHead(e.target.value)} />
        <div></div> {/* ÿ™⁄©ŸÖ€åŸÑ ÿ±ÿØ€åŸÅ */}
      </div>

      <div className="text-center mt-6">
        <button
          onClick={handleSubmit}
          className="px-6 py-2 bg-green-600 text-white rounded-xl hover:bg-green-700 transition"
        >
          ÿ∞ÿÆ€åÿ±Ÿá ŸÅÿ±ŸÖ
        </button>
      </div>
    </div>
  );
}

