}

// ÿß€åŸÜ ŸáŸÖÿßŸÜ ResidentDetails ŸàŸÑ€å ÿ®ÿ±ÿß€å Trainer
export default function TrainerDetails({
  trainerId,
  onClose,
}: TrainerDetailsProps) {
  const [selectedForm, setSelectedForm] = useState<string | null>(null);

  // ÿß€åŸÜÿ¨ÿß ÿßÿ≤ API ÿ™ÿ±€åŸÜÿ± ÿßÿ≥ÿ™ŸÅÿßÿØŸá ŸÖ€å‚Äå⁄©ŸÜ€åŸÖ
  const { data: trainer, isLoading } = useQuery({
    queryKey: ["/api/trainers", trainerId],
    queryFn: () => fetch(`/api/trainers/${trainerId}`).then((r) => r.json()),
  });

  if (isLoading) return <div>ÿØÿ± ÿ≠ÿßŸÑ ÿ®ÿßÿ±⁄Øÿ∞ÿßÿ±€å...</div>;
  if (!trainer) return <div>ÿ™ÿ±€åŸÜÿ± Ÿæ€åÿØÿß ŸÜÿ¥ÿØ.</div>;

  return (
    <div className="relative bg-white rounded-lg shadow-lg border border-slate-200 p-6">
      {/* ÿØ⁄©ŸÖŸá ÿ®ÿ≥ÿ™ŸÜ */}
      <Button
        variant="ghost"
        size="sm"
        className="absolute top-4 right-4"
        onClick={onClose}
      >
        <X className="h-4 w-4" />
      </Button>

      {/* ÿ±ÿØ€åŸÅ ÿ®ÿßŸÑÿß: ÿπ⁄©ÿ≥ + ÿØ⁄©ŸÖŸá ŸÅÿ±ŸÖ‚ÄåŸáÿß + ÿß⁄©ÿ¥ŸÜ */}
      <div className="flex items-center justify-between mb-4 w-full">
        <div className="flex-shrink-0 w-24 h-24 rounded-full border border-slate-300 overflow-hidden">
          {trainer.profileImageUrl ? (
            <img
              src={trainer.profileImageUrl}
              alt={`${trainer.name} ${trainer.lastName}`}
              className="w-full h-full object-cover"
            />
          ) : (
            <div className="w-full h-full flex items-center justify-center bg-slate-200 text-slate-500">
              ÿπ⁄©ÿ≥
            </div>
          )}
        </div>

        <div className="flex-1 flex justify-center space-x-4 overflow-x-auto mx-4">
          {FORM_TYPES.map((ft) => (
            <Button
              key={ft.type}
              onClick={() => setSelectedForm(ft.type)}
              className={`w-16 h-16 rounded-full flex items-center justify-center text-sm font-semibold
                ${
                  selectedForm === ft.type
                    ? "bg-blue-500 text-white"
                    : "bg-slate-100 text-slate-700"
                }
                hover:bg-slate-200 transition`}
              title={ft.name}
            >
              {ft.type}
            </Button>
          ))}
        </div>

        <div className="flex-shrink-0">
          <Button size="sm" className="bg-red-500 text-white hover:bg-red-600">
            Disciplinary Actions
          </Button>
        </div>
      </div>

      {/* ÿßÿ∑ŸÑÿßÿπÿßÿ™ ÿ™ÿ±€åŸÜÿ± */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4 border-t border-slate-200 pt-4 mt-4">
        <div>
          <h4 className="font-medium text-slate-900 mb-2">ÿßÿ∑ŸÑÿßÿπÿßÿ™ ÿ¥ÿÆÿµ€å</h4>
          <ul className="text-sm text-slate-700 space-y-1">
            <li>
              <strong>ŸÜÿßŸÖ ⁄©ÿßŸÖŸÑ:</strong> {trainer.name} {trainer.lastName}
            </li>
            <li>
              <strong>ÿ¨ŸÜÿ≥€åÿ™:</strong> {trainer.gender}
            </li>
            <li>
              <strong>ÿ¥ŸÖÿßÿ±Ÿá ÿ™ŸÖÿßÿ≥:</strong> {trainer.phoneNumber}
            </li>
          </ul>
        </div>
        <div>
          <h4 className="font-medium text-slate-900 mb-2">ÿßÿ∑ŸÑÿßÿπÿßÿ™ ÿ¢ŸÖŸàÿ≤ÿ¥€å</h4>
          <ul className="text-sm text-slate-700 space-y-1">
            <li>
              <strong>ÿØ€åŸæÿßÿ±ÿ™ŸÖŸÜÿ™:</strong> {trainer.department}
            </li>
            <li>
              <strong>ÿ™ÿßÿ±€åÿÆ ÿ¥ÿ±Ÿàÿπ:</strong> {trainer.joiningDate}
            </li>
            <li>
              <strong>ÿ≥ÿßŸÑ ÿ¢ŸÖŸàÿ≤ÿ¥€å:</strong> {trainer.trainingYear}
            </li>
          </ul>
        </div>
      </div>

      {/* ÿØ€åÿßŸÑŸà⁄Ø ÿ®ÿ±ÿß€å ŸÅÿ±ŸÖ */}
      <Dialog open={!!selectedForm} onOpenChange={() => setSelectedForm(null)}>
        <DialogContent
          className="w-[60%] max-w-none 
                  max-h-[90vh] overflow-y-auto 
                  mt-10 mx-auto
                  p-4 bg-white rounded-lg"
        >
          <DialogHeader>
            <DialogTitle>ÿ¨ÿ≤ÿ¶€åÿßÿ™ ŸÅÿ±ŸÖ {selectedForm}</DialogTitle>
          </DialogHeader>

          {selectedForm === "C" && (
            <FormCDetails
              residentId={trainerId}
              onClose={() => setSelectedForm(null)}
            />
          )}
          {selectedForm === "I" && (
            <RotationForm
              trainerId={trainerId}
              onClose={() => setSelectedForm(null)}
            />
          )}
          {selectedForm === "J" && (
            <TeacherActivityForm
              trainerId={trainerId}
              onClose={() => setSelectedForm(null)}
            />
          )}
          {selectedForm === "D" && (
            <FormDDetails
              residentId={trainerId}
              onClose={() => setSelectedForm(null)}
            />
          )}
          {selectedForm === "E" && (
            <FormEDetails
              residentId={trainerId}
              onClose={() => setSelectedForm(null)}
            />
          )}
          {selectedForm === "G" && (
            <FormGDetails
              residentId={trainerId}
              onClose={() => setSelectedForm(null)}
            />
          )}
          {selectedForm === "H" && (
            <FormHDetails
              residentId={trainerId}
              onClose={() => setSelectedForm(null)}
            />
          )}

          {selectedForm === "K" && (
            <FormKDetails
              residentId={trainerId} // €åÿß residentId
              onClose={() => setSelectedForm(null)}
            />
          )}
          {/* ÿ®ŸÇ€åŸá ŸÅÿ±ŸÖ‚ÄåŸáÿß ÿ±ÿß ŸáŸÖ ŸÖÿ¥ÿßÿ®Ÿá ÿßÿ∂ÿßŸÅŸá ⁄©ŸÜ€åÿØ */}
        </DialogContent>
      </Dialog>
    </div>
  );
}

server/models/form-I.ts.content:
import mongoose, { Schema, Document } from "mongoose";

interface IWeek {
  cases: number;
  level: "Basic" | "Intermediate" | "Advanced" | "";
}

interface IEnglishRow {
  weeks: IWeek[];
  total: number;
}

interface IPersianRow {
  mark: string;
  teacherName: string;
  teacherSign: string;
  note: string;
}

export interface IRotationForm extends Document {
  trainerId: mongoose.Schema.Types.ObjectId; // ÿßÿ∂ÿßŸÅŸá ÿ¥ÿØ
  header: {
    studentName: string;
    department: string;
    rotationFrom: string;
    rotationTo: string;
    date: string;
  };
  persianRows: IPersianRow[];
  persianNote: string;
  rotationName: string;
  rows: IEnglishRow[];
  createdAt: Date;
}

const WeekSchema = new Schema<IWeek>({
  cases: { type: Number, default: 0 },
  level: { type: String, enum: ["Basic", "Intermediate", "Advanced", ""], default: "" },
});

const EnglishRowSchema = new Schema<IEnglishRow>({
  weeks: { type: [WeekSchema], default: [] },
  total: { type: Number, default: 0 },
});

const PersianRowSchema = new Schema<IPersianRow>({
  mark: String,
  teacherName: String,
  teacherSign: String,
  note: String,
});

const RotationFormSchema = new Schema<IRotationForm>({
  trainerId: { type: mongoose.Schema.Types.ObjectId, ref: "Trainer", required: true }, // ÿßÿ∂ÿßŸÅŸá ÿ¥ÿØ
  header: {
    studentName: String,
    department: String,
    rotationFrom: String,
    rotationTo: String,
    date: String,
  },
  persianRows: [PersianRowSchema],
  persianNote: String,
  rotationName: String,
  rows: [EnglishRowSchema],
  createdAt: { type: Date, default: Date.now },
});

export default mongoose.model<IRotationForm>("RotationForm", RotationFormSchema);

