import express, { Request, Response } from "express";
import RotationForm, { IRotationForm } from "../models/form-I";
import mongoose from "mongoose";

const router = express.Router();

// üîπ ÿ∞ÿÆ€åÿ±Ÿá ŸÅÿ±ŸÖ ÿ®ÿ±ÿß€å €å⁄© ÿ™ÿ±€åŸÜÿ± ŸÖÿ¥ÿÆÿµ
router.post("/", async (req: Request, res: Response) => {
  try {
    const { trainerId, header, persianRows, persianNote, rotationName, rows } = req.body;

    if (!trainerId) {
      return res.status(400).json({ error: "TrainerId ÿßŸÑÿ≤ÿßŸÖ€å ÿßÿ≥ÿ™" });
    }

    const form: IRotationForm = new RotationForm({
      trainerId: new mongoose.Types.ObjectId(trainerId), // üëà ŸÖŸáŸÖ
      header,
      persianRows,
      persianNote,
      rotationName,
      rows,
    });

    await form.save();
    res.status(201).json({ message: "‚úÖ ŸÅÿ±ŸÖ ÿ∞ÿÆ€åÿ±Ÿá ÿ¥ÿØ", id: form._id });
  } catch (err) {
    console.error(err);
    res.status(500).json({ error: "‚ùå ÿÆÿ∑ÿß ÿØÿ± ÿ∞ÿÆ€åÿ±Ÿá ŸÅÿ±ŸÖ" });
  }
});

// üîπ ⁄Øÿ±ŸÅÿ™ŸÜ ŸÑ€åÿ≥ÿ™ ŸÅÿ±ŸÖ‚ÄåŸáÿß€å €å⁄© ÿ™ÿ±€åŸÜÿ± ŸÖÿ¥ÿÆÿµ
router.get("/:trainerId", async (req: Request, res: Response) => {
  try {
    const { trainerId } = req.params;

    if (!trainerId) {
      return res.status(400).json({ error: "TrainerId ÿßŸÑÿ≤ÿßŸÖ€å ÿßÿ≥ÿ™" });
    }

    const forms = await RotationForm.find({
      trainerId: new mongoose.Types.ObjectId(trainerId), // üëà ŸÖŸáŸÖ
    }).sort({ createdAt: -1 });

    res.json(forms);
  } catch (err) {
    console.error(err);
    res.status(500).json({ error: "‚ùå ÿÆÿ∑ÿß ÿØÿ± ⁄Øÿ±ŸÅÿ™ŸÜ ŸÅÿ±ŸÖ‚ÄåŸáÿß" });
  }
});


export default router;

