import { useQuery } from "@tanstack/react-query";
import * as XLSX from "xlsx";
import { useRef } from "react";
import { useTrainer } from "../../../context/TrainerContext";

interface FormKDetailsProps {
  residentId?: string;
  onClose: () => void;
}

export default function MonographEvaluationDisplay({
  residentId,
  onClose,
}: FormKDetailsProps) {
  const { trainerId } = useTrainer();
  const { data, isLoading } = useQuery<any>({
    queryKey: ["/api/monographEvaluation", trainerId],
    queryFn: () =>
      fetch(`/api/monographEvaluation?trainerId=${trainerId}`).then((r) =>
        r.json()
      ),
    enabled: !!trainerId,
  });

  const printRef = useRef<HTMLDivElement>(null);

  if (isLoading) return <div>ÿØÿ± ÿ≠ÿßŸÑ ÿ®ÿßÿ±⁄Øÿ∞ÿßÿ±€å...</div>;
  if (!data || !data.length) return <div>Ÿá€å⁄Ü ŸÅÿ±ŸÖ€å ŸÖŸàÿ¨ŸàÿØ ŸÜ€åÿ≥ÿ™</div>;

  // Ÿæ€åÿØÿß ⁄©ÿ±ÿØŸÜ ŸÅÿ±ŸÖ ÿ®ÿ± ÿßÿ≥ÿßÿ≥ trainerId €åÿß residentId
  const form = data.find(
    (f: any) => f.trainer === residentId || f.trainer?._id === residentId
  );

  if (!form) return <div>ŸÅÿ±ŸÖ ŸÖŸàÿ±ÿØ ŸÜÿ∏ÿ± Ÿæ€åÿØÿß ŸÜÿ¥ÿØ.</div>;

  const handlePrint = () => {
    if (printRef.current) {
      const printContent = printRef.current.innerHTML;
      const newWin = window.open("", "_blank");
      if (newWin) {
        newWin.document.write(`
          <html>
            <head>
              <title>⁄ÜÿßŸæ ŸÅÿ±ŸÖ ŸÖŸàŸÜŸà⁄Øÿ±ÿßŸÅ</title>
              <style>
                table { border-collapse: collapse; width: 100%; }
                th, td { border: 1px solid #333; padding: 4px; text-align: center; }
                th { background: #eee; }
                body { font-family: sans-serif; direction: rtl; }
              </style>
            </head>
            <body>${printContent}</body>
          </html>
        `);
        newWin.document.close();
        newWin.print();
      }
    }
  };

  const exportExcel = () => {
    const wsData: any[] = [
      [
        "ÿ®ÿÆÿ¥",
        "ŸÅ€åÿµÿØ€å",
        "ŸÜŸÖÿ±Ÿá ÿØÿßÿØŸá ÿ¥ÿØŸá",
        "ÿßÿ≥ŸÖ ÿßÿ≥ÿ™ÿßÿØ",
        "ÿßŸÖÿ∂ÿß",
        "Ÿà€å⁄ò⁄Ø€å‚ÄåŸáÿß",
        "ŸÖÿ¨ŸÖŸàÿπ",
        "ŸÖ€åÿßŸÜ⁄Ø€åŸÜ",
        "€åÿßÿØÿØÿßÿ¥ÿ™‚ÄåŸáÿß",
      ],
      ...form.evaluations.map((e: any) => [
        e.section,
        e.percentage,
        e.score,
        e.teacherName,
        e.teacherSigned ? "ÿ®ŸÑŸá" : "ÿÆ€åÿ±",
        e.characteristics,
        e.total,
        e.average,
        e.notes,
      ]),
    ];

    const wb = XLSX.utils.book_new();
    const ws = XLSX.utils.aoa_to_sheet(wsData);
    XLSX.utils.book_append_sheet(wb, ws, "MonographEvaluation");
    XLSX.writeFile(wb, `Monograph_${form.name}_${form.fatherName}.xlsx`);
  };

  return (
    <div className="p-4">
      <div ref={printRef}>
        <h3 className="text-lg font-bold mb-2 text-center">
          ŸÅÿ±ŸÖ ÿßÿ±ÿ≤€åÿßÿ®€å ŸÖŸàŸÜŸà⁄Øÿ±ÿßŸÅ ‚Äì {form.name} {form.fatherName}
        </h3>

        {/* ŸÖÿ¥ÿÆÿµÿßÿ™ ÿØŸà ÿ±ÿØ€åŸÅ */}
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4 mb-4">
          <input
            type="text"
            value={form.name}
            readOnly
            className="border px-2 py-1 w-full text-center"
          />
          <input
            type="text"
            value={form.lastName}
            readOnly
            className="border px-2 py-1 w-full text-center"
          />
          <input
            type="text"
            value={form.fatherName}
            readOnly
            className="border px-2 py-1 w-full text-center"
          />
          <input
            type="text"
            value={form.idNumber}
            readOnly
            className="border px-2 py-1 w-full text-center"
          />
          <input
            type="text"
            value={form.field}
            readOnly
            className="border px-2 py-1 w-full text-center"
          />
          <input
            type="text"
            value={form.trainingYear}
            readOnly
            className="border px-2 py-1 w-full text-center"
          />
          <input
            type="text"
            value={form.startYear}
            readOnly
            className="border px-2 py-1 w-full text-center"
          />
          <input
            type="text"
            value={form.date}
            readOnly
            className="border px-2 py-1 w-full text-center"
          />
        </div>

        {/* ÿ¨ÿØŸàŸÑ ÿßÿ±ÿ≤€åÿßÿ®€å */}
        {/* ÿ¨ÿØŸàŸÑ ÿßÿ±ÿ≤€åÿßÿ®€å */}
        <table className="table-auto border-collapse border w-full text-center mb-4">
          <thead>
            <tr>
              <th>ÿ®ÿÆÿ¥</th>
              <th>ŸÅ€åÿµÿØ€å</th>
              <th>ŸÜŸÖÿ±Ÿá ÿØÿßÿØŸá ÿ¥ÿØŸá</th>
              <th>ÿßÿ≥ŸÖ ÿßÿ≥ÿ™ÿßÿØ</th>
            </tr>
          </thead>
          <tbody>
            {form.evaluations.map((e: any, idx: number) => (
              <tr key={idx}>
                <td>{e.section}</td>
                <td>{e.percentage}</td>
                <td>{e.score}</td>
                <td>{e.teacherName}</td>

              </tr>
            ))}

            {/* ÿ±ÿØ€åŸÅ ŸÖÿ¨ŸÖŸàÿπ Ÿà ÿßŸàÿ≥ÿ∑ */}
            <tr>
              <td  className="border px-2 py-2 font-bold">
                ŸÖÿ¨ŸÖŸàÿπ
              </td>
              <td  className="border px-2 py-2">
                {form.evaluations[0]?.total}
              </td>
              <td  className="border px-2 py-2 font-bold">
                ÿßŸàÿ≥ÿ∑
              </td>
              <td  className="border px-2 py-2">
                {form.evaluations[0]?.average}
              </td>

            </tr>


            {/* ÿ±ÿØ€åŸÅ €åÿßÿØÿØÿßÿ¥ÿ™‚ÄåŸáÿß */}
            <tr>
              <td colSpan={6} className="border px-2 py-2 text-right">
                {form.evaluations[0]?.notes}
              </td>
            </tr>
          </tbody>
        </table>
      </div>

      <div className="mt-4 flex gap-2">
        <button
          className="bg-blue-500 text-white px-3 py-1 rounded"
          onClick={handlePrint}
        >
          ⁄ÜÿßŸæ PDF
        </button>
        <button
          className="bg-green-500 text-white px-3 py-1 rounded"
          onClick={exportExcel}
        >
          ÿÆÿ±Ÿàÿ¨€å Excel
        </button>
        <button
          className="bg-gray-500 text-white px-3 py-1 rounded"
          onClick={onClose}
        >
          ÿ®ÿ≥ÿ™ŸÜ
        </button>
      </div>
    </div>
  );
}

