import * as React from "react";
import { Dialog, DialogContent, DialogHeader, DialogTitle, DialogFooter } from "@/components/ui/dialog";
import { Input } from "@/components/ui/input";
import { Button } from "@/components/ui/button";
import { useState, useEffect } from "react";
import { useToast } from "@/hooks/use-toast";
import axios from "axios";
import { User } from "@shared/schema";

interface UserFormDialogProps {
  isOpen: boolean;
  onClose: () => void;
  onSuccess: () => void;
  editingUser?: User | null;
}

export default function UserFormDialog({ isOpen, onClose, onSuccess, editingUser }: UserFormDialogProps) {
  const { toast } = useToast();
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("viewer");
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (editingUser) {
      setFirstName(editingUser.firstName || "");
      setLastName(editingUser.lastName || "");
      setEmail(editingUser.email || "");
      setRole(editingUser.role || "viewer");
      setPassword("");
    } else {
      setFirstName("");
      setLastName("");
      setEmail("");
      setPassword("");
      setRole("viewer");
    }
  }, [editingUser, isOpen]);

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    try {
      if (editingUser) {
        const updateData: any = { firstName, lastName, email, role };
        if (password) {
          updateData.password = password;
        }
        await axios.put(`/api/users/${editingUser._id}`, updateData);
        toast({ title: "ŸÖŸàŸÅŸÇ€åÿ™", description: "⁄©ÿßÿ±ÿ®ÿ± ÿ®Ÿá‚Äåÿ±Ÿàÿ≤ÿ±ÿ≥ÿßŸÜ€å ÿ¥ÿØ" });
      } else {
        if (!password) {
          toast({ title: "ÿÆÿ∑ÿß", description: "ÿ±ŸÖÿ≤ ÿπÿ®Ÿàÿ± ÿßŸÑÿ≤ÿßŸÖ€å ÿßÿ≥ÿ™", variant: "destructive" });
          return;
        }
        await axios.post("/api/users", { firstName, lastName, email, password, role });
        toast({ title: "ŸÖŸàŸÅŸÇ€åÿ™", description: "⁄©ÿßÿ±ÿ®ÿ± ÿ¨ÿØ€åÿØ ÿ´ÿ®ÿ™ ÿ¥ÿØ" });
      }
      setFirstName("");
      setLastName("");
      setEmail("");
      setPassword("");
      setRole("viewer");
      onSuccess();
      onClose();
    } catch (err: any) {
      toast({ 
        title: "ÿÆÿ∑ÿß", 
        description: err.response?.data?.message || err.message || "ÿπŸÖŸÑ€åÿßÿ™ ÿ®ÿß ÿÆÿ∑ÿß ŸÖŸàÿßÿ¨Ÿá ÿ¥ÿØ", 
        variant: "destructive" 
      });
    } finally {
      setLoading(false);
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={(open) => !open && onClose()}>
      <DialogContent className="sm:max-w-lg">
        <DialogHeader>
          <DialogTitle>{editingUser ? "Ÿà€åÿ±ÿß€åÿ¥ ⁄©ÿßÿ±ÿ®ÿ±" : "ÿßŸÅÿ≤ŸàÿØŸÜ ⁄©ÿßÿ±ÿ®ÿ± ÿ¨ÿØ€åÿØ"}</DialogTitle>
        </DialogHeader>
        <form onSubmit={handleSubmit} className="space-y-4">
          <div>
            <label className="block mb-1 font-medium">ŸÜÿßŸÖ</label>
            <Input value={firstName} onChange={(e) => setFirstName(e.target.value)} required />
          </div>
          <div>
            <label className="block mb-1 font-medium">ŸÜÿßŸÖ ÿÆÿßŸÜŸàÿßÿØ⁄Ø€å</label>
            <Input value={lastName} onChange={(e) => setLastName(e.target.value)} />
          </div>
          <div>
            <label className="block mb-1 font-medium">ÿß€åŸÖ€åŸÑ</label>
            <Input type="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
          </div>
          <div>
            <label className="block mb-1 font-medium">
              ÿ±ŸÖÿ≤ ÿπÿ®Ÿàÿ± {editingUser && <span className="text-sm text-gray-500">(ÿ®ÿ±ÿß€å ÿ™ÿ∫€å€åÿ± ÿ±ŸÖÿ≤ Ÿàÿßÿ±ÿØ ⁄©ŸÜ€åÿØ)</span>}
            </label>
            <Input 
              type="password" 
              value={password} 
              onChange={(e) => setPassword(e.target.value)} 
              required={!editingUser}
              placeholder={editingUser ? "ÿ®ÿ±ÿß€å ÿ™ÿ∫€å€åÿ± ÿ±ŸÖÿ≤ Ÿàÿßÿ±ÿØ ⁄©ŸÜ€åÿØ" : "ÿ±ŸÖÿ≤ ÿπÿ®Ÿàÿ±"}
            />
          </div>
          <div>
            <label className="block mb-1 font-medium">ŸÜŸÇÿ¥</label>
            <select
              value={role}
              onChange={(e) => setRole(e.target.value)}
              className="w-full border rounded-md p-2 bg-white"
              required
            >
              <option value="viewer">ÿ®€åŸÜŸÜÿØŸá (Viewer)</option>
              <option value="admin">ŸÖÿØ€åÿ± (Admin)</option>
            </select>
          </div>
          <DialogFooter>
            <Button type="button" variant="outline" onClick={onClose} disabled={loading}>
              ŸÑÿ∫Ÿà
            </Button>
            <Button type="submit" disabled={loading}>
              {loading ? "ÿØÿ± ÿ≠ÿßŸÑ ÿßÿ±ÿ≥ÿßŸÑ..." : editingUser ? "ÿ®Ÿá‚Äåÿ±Ÿàÿ≤ÿ±ÿ≥ÿßŸÜ€å" : "ÿßŸÅÿ≤ŸàÿØŸÜ ⁄©ÿßÿ±ÿ®ÿ±"}
            </Button>
          </DialogFooter>
        </form>
      </DialogContent>
    </Dialog>
  );
}

