import { useState } from "react";
import { useQuery } from "@tanstack/react-query";
import { Button } from "@/components/ui/button";
import { Card, CardContent } from "@/components/ui/card";
import { Users, FileText, ChevronLeft } from "lucide-react";
import { apiRequest } from "@/lib/queryClient";
import TeachersReport from "./TeacherReportTable"; 
import FormsReport from "./TrainerFormsReport"; 
import TrainerReports from "./TrainerReports"; // üëâ ÿß€åŸÜŸà ÿßÿ∂ÿßŸÅŸá ⁄©ŸÜ

const reports = [
  {
    title: "⁄Øÿ≤ÿßÿ±ÿ¥ÿßÿ™ ÿ™ÿ±€åŸÜÿ±€å",
    icon: Users,
    color: "bg-hospital-green-100 text-hospital-green-600",
    buttonColor: "bg-hospital-green-600 hover:bg-hospital-green-700",
  },
  // {
  //   title: "⁄Øÿ≤ÿßÿ±ÿ¥ÿßÿ™ ŸÅŸàÿ±ŸÖ Ÿáÿß",
  //   icon: FileText,
  //   color: "bg-blue-100 text-blue-600",
  //   buttonColor: "bg-blue-600 hover:bg-blue-700",
  // },
  {
    title: "⁄Øÿ≤ÿßÿ±ÿ¥ÿßÿ™ ÿßÿ≥ÿ™ÿßÿØÿßŸÜ",
    icon: Users,
    color: "bg-purple-100 text-purple-600",
    buttonColor: "bg-purple-600 hover:bg-purple-700",
  },
];

export default function ReportCards() {
  const [showTeachersReport, setShowTeachersReport] = useState(false);
  const [showFormsReport, setShowFormsReport] = useState(false);
  const [showTrainerReport, setShowTrainerReport] = useState(false); // üëâ ÿßÿ≥ÿ™€åÿ™ ÿ¨ÿØ€åÿØ

  // ⁄Øÿ±ŸÅÿ™ŸÜ ŸÑ€åÿ≥ÿ™ ÿßÿ≥ÿ™ÿßÿØÿßŸÜ ÿßÿ≤ API
  const { data: teachers = [], isLoading, error } = useQuery({
    queryKey: ["/api/teachers"],
    queryFn: async () => {
      const res = await apiRequest("/api/teachers");
      return res.json();
    },
  });

  const handleGenerateReport = (reportType: string) => {
    if (reportType === "⁄Øÿ≤ÿßÿ±ÿ¥ÿßÿ™ ÿßÿ≥ÿ™ÿßÿØÿßŸÜ") {
      setShowTeachersReport(true);
      setShowFormsReport(false);
      setShowTrainerReport(false);
    } 
    // else if (reportType === "⁄Øÿ≤ÿßÿ±ÿ¥ÿßÿ™ ŸÅŸàÿ±ŸÖ Ÿáÿß") {
    //   setShowFormsReport(true);
    //   setShowTeachersReport(false);
    //   setShowTrainerReport(false);
    // } 
    else if (reportType === "⁄Øÿ≤ÿßÿ±ÿ¥ÿßÿ™ ÿ™ÿ±€åŸÜÿ±€å") {
      setShowTrainerReport(true);
      setShowTeachersReport(false);
      setShowFormsReport(false);
    } else {
      setShowTeachersReport(false);
      setShowFormsReport(false);
      setShowTrainerReport(false);
    }
  };

  return (
    <div>
      {/* ⁄©ÿßÿ±ÿ™‚ÄåŸáÿß ŸÅŸÇÿ∑ ŸàŸÇÿ™€å Ÿá€å⁄Ü ⁄Øÿ≤ÿßÿ±ÿ¥ ÿØ€å⁄Øÿ±€å ŸÜŸÖÿß€åÿ¥ ÿØÿßÿØŸá ŸÜŸÖ€å‚Äåÿ¥ŸàÿØ */}
      {!showTeachersReport && !showFormsReport && !showTrainerReport && (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {reports.map((report) => {
            const Icon = report.icon;
            return (
              <Card key={report.title} className="hover:shadow-md transition-shadow">
                <CardContent className="p-6">
                  <div className="flex items-center mb-4">
                    <div className={`p-2 rounded-lg ${report.color}`}>
                      <Icon className="h-6 w-6" />
                    </div>
                    <div className="ml-4">
                      <h3 className="text-lg font-semibold text-slate-900">{report.title}</h3>
                    </div>
                  </div>
                  <Button
                    onClick={() => handleGenerateReport(report.title)}
                    className={`w-full text-white ${report.buttonColor}`}
                  >
                    ÿØ€åÿØŸÜ ⁄Øÿ≤ÿßÿ±ÿ¥
                  </Button>
                </CardContent>
              </Card>
            );
          })}
        </div>
      )}

      {/* ŸÜŸÖÿß€åÿ¥ ÿ¨ÿØŸàŸÑ ÿßÿ≥ÿ™ÿßÿØÿßŸÜ */}
      {showTeachersReport && (
        <div className="mt-6">
          <div className="mb-4">
            <Button
              variant="outline"
              size="sm"
              onClick={() => setShowTeachersReport(false)}
              className="flex items-center gap-2"
            >
              <ChevronLeft className="h-4 w-4" />
              ÿ®ÿßÿ≤⁄Øÿ¥ÿ™ ÿ®Ÿá ⁄Øÿ≤ÿßÿ±ÿ¥ÿßÿ™
