import { Request, Response } from "express";
import { EvaluationFormE } from "../models/form-E";

// ÿß€åÿ¨ÿßÿØ ŸÅÿ±ŸÖ ÿ¨ÿØ€åÿØ
export const createEvaluationFormE = async (req: Request, res: Response) => {
  try {
    const {
      trainer,
      residentName,
      fatherName,
      trainingYear,
      incidentTitle,
      date,
      scores,            // üëà ÿ¢ÿ±ÿß€åŸá ŸÖ€å‚Äå⁄Ø€åÿ±€åŸÖ
      averageScore,
    } = req.body;

    if (!trainer) {
      return res.status(400).json({ message: "Trainer ID ÿßŸÑÿ≤ÿßŸÖ€å ÿßÿ≥ÿ™" });
    }

    const form = new EvaluationFormE({
      trainer,
      residentName,
      fatherName,
      trainingYear,
      incidentTitle,
      date,
      scores,
      averageScore,
    });

    const saved = await form.save();
    res.status(201).json(saved);
  } catch (err: any) {
    console.error("Error saving EvaluationFormE:", err);
    res.status(500).json({ message: err.message || "ÿÆÿ∑ÿß ÿØÿ± ÿ∞ÿÆ€åÿ±Ÿá ŸÅÿ±ŸÖ" });
  }
};


// ⁄Øÿ±ŸÅÿ™ŸÜ ŸÅÿ±ŸÖ‚ÄåŸáÿß ÿ®ÿ± ÿßÿ≥ÿßÿ≥ trainerId
export const getEvaluationFormsE = async (req: Request, res: Response) => {
  try {
    const { trainerId } = req.query;
    if (!trainerId) {
      return res.status(400).json({ message: "Trainer ID ÿßŸÑÿ≤ÿßŸÖ€å ÿßÿ≥ÿ™" });
