// components/forms/FormCDetails.tsx
import { useQuery } from "@tanstack/react-query";
import { useRef } from "react";
import * as XLSX from "xlsx";

interface FormCDetailsProps {
  residentId: string;
  onClose: () => void;
}

export default function FormCDetails({
  residentId,
  onClose,
}: FormCDetailsProps) {
  const { data, isLoading } = useQuery<any>({
    queryKey: ["/api/monograph", residentId, "C"],
    queryFn: () =>
      fetch(`/api/monograph?residentId=${residentId}&type=C`).then((r) =>
        r.json()
      ),
  });

  const printRef = useRef<HTMLDivElement>(null);

  if (isLoading) return <div>ÿØÿ± ÿ≠ÿßŸÑ ÿ®ÿßÿ±⁄Øÿ∞ÿßÿ±€å...</div>;
  if (!data || !data.data?.length) return <div>ŸÅÿ±ŸÖ Ÿæ€åÿØÿß ŸÜÿ¥ÿØ.</div>;

  const form = data.data[0];

  // ⁄ÜÿßŸæ PDF ÿßÿ≤ ÿ∑ÿ±€åŸÇ ŸÖÿ±Ÿàÿ±⁄Øÿ± (ÿØŸÇ€åŸÇÿßŸã ŸáŸÖÿßŸÜ HTML ŸÅÿ±ŸÖ)
  const printForm = () => {
    if (printRef.current) {
      const printContents = printRef.current.innerHTML;
      const newWindow = window.open("", "_blank", "width=900,height=600");
      if (newWindow) {
        newWindow.document.write(`
          <html>
            <head>
              <title>ŸÅÿ±ŸÖ C ‚Äì ${form.name} ${form.lastName}</title>
              <style>
                body { font-family: Arial, sans-serif; direction: rtl; }
                h3 { text-align: center; margin-bottom: 20px; }
                table { width: 100%; border-collapse: collapse; margin-bottom: 20px; }
                th, td { border: 1px solid #333; padding: 6px; text-align: right; }
                th { background-color: #f3f3f3; }
              </style>
            </head>
            <body>${printContents}</body>
          </html>
        `);
        newWindow.document.close();
        newWindow.focus();
        newWindow.print();
      }
    }
  };

  // ÿÆÿ±Ÿàÿ¨€å Excel
  const exportExcel = () => {
    const wb = XLSX.utils.book_new();

    // ŸÖÿ¥ÿÆÿµÿßÿ™ ŸÅÿ±ÿØ€å
    const detailsWS = XLSX.utils.json_to_sheet([
      { ŸÅ€åŸÑÿØ: "ŸÜÿßŸÖ", ŸÖŸÇÿØÿßÿ±: form.name },
      { ŸÅ€åŸÑÿØ: "ŸÜÿßŸÖ ÿÆÿßŸÜŸàÿßÿØ⁄Ø€å", ŸÖŸÇÿØÿßÿ±: form.lastName },
      { ŸÅ€åŸÑÿØ: "ŸæÿØÿ±", ŸÖŸÇÿØÿßÿ±: form.fatherName },
      { ŸÅ€åŸÑÿØ: "ŸÜŸÖÿ®ÿ± ÿ™ÿ∞⁄©ÿ±Ÿá", ŸÖŸÇÿØÿßÿ±: form.idNumber },
      { ŸÅ€åŸÑÿØ: "ÿ±ÿ¥ÿ™Ÿá", ŸÖŸÇÿØÿßÿ±: form.field },
      { ŸÅ€åŸÑÿØ: "ÿ≥ÿßŸÑ ÿ™ÿ±€åŸÜŸÜ⁄Ø", ŸÖŸÇÿØÿßÿ±: form.trainingYear },
      { ŸÅ€åŸÑÿØ: "ÿ≥ÿßŸÑ ÿ¥ŸÖŸàŸÑ", ŸÖŸÇÿØÿßÿ±: form.startYear },
      { ŸÅ€åŸÑÿØ: "ÿ™ÿßÿ±€åÿÆ", ŸÖŸÇÿØÿßÿ±: form.date },
      { ŸÅ€åŸÑÿØ: "ÿ¥ŸÅ", ŸÖŸÇÿØÿßÿ±: form.chef },
