// components/forms/FormDDetails.tsx
import { useQuery } from "@tanstack/react-query";
import * as XLSX from "xlsx";
import { useTrainer } from "../../../context/TrainerContext"; // ‚Üê ŸÖÿ´ŸÑÿßŸã
import { useRef } from "react";

interface FormDDetailsProps {
  residentId: string;
  onClose: () => void;
}

export default function FormDDetails({residentId, onClose }: FormDDetailsProps) {
  const { trainerId } = useTrainer();
  const printRef = useRef<HTMLDivElement>(null);

  const { data, isLoading } = useQuery<any>({
    queryKey: ["conferenceForms", trainerId],
    queryFn: async () => {
      if (!trainerId) return [];
      const res = await fetch(`/api/conference?trainerId=${trainerId}`);
      return res.json();
    },
    enabled: !!trainerId,
  });

  if (!trainerId) return <div>Trainer ID ÿÆÿßŸÑ€å ÿßÿ≥ÿ™.</div>;
  if (isLoading) return <div>ÿØÿ± ÿ≠ÿßŸÑ ÿ®ÿßÿ±⁄Øÿ∞ÿßÿ±€å...</div>;
  if (!data || !data.length) return <div>ŸÅÿ±ŸÖ€å Ÿæ€åÿØÿß ŸÜÿ¥ÿØ.</div>;

  const form = data[0];

  const exportExcel = () => {
    const wsData: any[] = [];

    // ŸÖÿ¥ÿÆÿµÿßÿ™ ŸÅÿ±ÿØ€å
    wsData.push(["ŸÜÿßŸÖ", form.name]);
    wsData.push(["ŸæÿØÿ±", form.fatherName]);
    wsData.push(["ÿØ€åŸæÿßÿ±ÿ™ŸÖŸÜÿ™", form.department]);
    wsData.push(["ÿ≥ÿßŸÑ ÿ¢ŸÖŸàÿ≤ÿ¥", form.trainingYear]);
    wsData.push([]);

    // ÿ¨ÿØŸàŸÑ ⁄©ŸÜŸÅÿ±ÿßŸÜÿ≥
    wsData.push(["ÿ¥ŸÖÿßÿ±Ÿá", "ŸÖŸàÿ∂Ÿàÿπ ⁄©ŸÜŸÅÿ±ÿßŸÜÿ≥", "ŸÜŸÖÿ±Ÿá ÿØÿßÿØŸá ÿ¥ÿØŸá", "ÿ™ÿßÿ±€åÿÆ ÿßÿ±ÿßÿ¶Ÿá", "ÿßÿ≥ŸÖ Ÿà ÿßŸÖÿ∂ÿß ÿßÿ≥ÿ™ÿßÿØ", "ŸÖŸÑÿßÿ≠ÿ∏ÿßÿ™"]);
    form.conferences.forEach((c: any, idx: number) => {
      wsData.push([idx + 1, c.conferenceTitle, c.score, c.date, c.teacherName, ""]);
    });
    wsData.push([]);

    // ÿ±ÿØ€åŸÅ Ÿæÿß€åÿßŸÜ€å ÿßŸÖÿ∂ÿßŸáÿß
    wsData.push(["ÿ±ÿ¶€åÿ≥ ÿØ€åŸæÿßÿ±ÿ™ŸÖŸÜÿ™", "ÿ¢ŸÖÿ± ÿ®ÿ±ŸÜÿßŸÖŸá ÿ™ÿ±€åŸÜŸÜ⁄Ø", "ÿ±ÿ¶€åÿ≥ ÿ¥ŸÅÿßÿÆÿßŸÜŸá"]);
    wsData.push([form.departmentHead || "", form.programHead || "", form.hospitalHead || ""]);

    const wb = XLSX.utils.book_new();
    const ws = XLSX.utils.aoa_to_sheet(wsData);
    XLSX.utils.book_append_sheet(wb, ws, "FormD");
    XLSX.writeFile(wb, `FormD_${form.name}_${form.fatherName}.xlsx`);
  };

  const handlePrint = () => {
    if (printRef.current) {
      const printContents = printRef.current.innerHTML;
      const originalContents = document.body.innerHTML;
      document.body.innerHTML = printContents;
      window.print();
      document.body.innerHTML = originalContents;
      window.location.reload(); // ÿ®ÿ±ÿß€å ÿ®ÿßÿ≤⁄Øÿ¥ÿ™ ÿ®Ÿá ÿ≠ÿßŸÑÿ™ ÿßŸàŸÑ€åŸá ÿµŸÅÿ≠Ÿá
    }
  };

  return (
    <div className="p-4">
      <div ref={printRef}>
        <h3 className="text-lg font-bold mb-2">ŸÅÿ±ŸÖ D ‚Äì {form.name} {form.fatherName}</h3>

        <table className="table-auto border-collapse border border-slate-300 text-sm w-full mb-4">
          <tbody>
            <tr><td className="border px-2 py-1 font-semibold">ŸÜÿßŸÖ</td><td className="border px-2 py-1">{form.name}</td></tr>
            <tr><td className="border px-2 py-1 font-semibold">ŸæÿØÿ±</td><td className="border px-2 py-1">{form.fatherName}</td></tr>
            <tr><td className="border px-2 py-1 font-semibold">ÿØ€åŸæÿßÿ±ÿ™ŸÖŸÜÿ™</td><td className="border px-2 py-1">{form.department}</td></tr>
            <tr><td className="border px-2 py-1 font-semibold">ÿ≥ÿßŸÑ ÿ¢ŸÖŸàÿ≤ÿ¥</td><td className="border px-2 py-1">{form.trainingYear}</td></tr>
          </tbody>
        </table>

        <table className="table-auto border-collapse border border-slate-300 text-sm w-full mb-4">
          <thead>
            <tr>
              <th className="border px-2 py-1">ÿ¥ŸÖÿßÿ±Ÿá</th>
              <th className="border px-2 py-1">ŸÖŸàÿ∂Ÿàÿπ ⁄©ŸÜŸÅÿ±ÿßŸÜÿ≥</th>
              <th className="border px-2 py-1">ŸÜŸÖÿ±Ÿá ÿØÿßÿØŸá ÿ¥ÿØŸá</th>
              <th className="border px-2 py-1">ÿ™ÿßÿ±€åÿÆ ÿßÿ±ÿßÿ¶Ÿá</th>
              <th className="border px-2 py-1">ÿßÿ≥ŸÖ Ÿà ÿßŸÖÿ∂ÿß ÿßÿ≥ÿ™ÿßÿØ</th>
              <th className="border px-2 py-1">ŸÖŸÑÿßÿ≠ÿ∏ÿßÿ™</th>
            </tr>
          </thead>
          <tbody>
            {form.conferences.map((c: any, idx: number) => (
              <tr key={idx}>
                <td className="border px-2 py-1">{idx + 1}</td>
                <td className="border px-2 py-1">{c.conferenceTitle}</td>
                <td className="border px-2 py-1">{c.score}</td>
                <td className="border px-2 py-1">{c.date}</td>
                <td className="border px-2 py-1">{c.teacherName}</td>
                <td className="border px-2 py-1"></td>
              </tr>
            ))}
          </tbody>
        </table>

        <table className="table-auto border-collapse border border-slate-300 text-sm w-full mb-4">
          <tbody>
            <tr>
              <td className="border px-2 py-1 font-semibold">ÿ±ÿ¶€åÿ≥ ÿØ€åŸæÿßÿ±ÿ™ŸÖŸÜÿ™</td>
              <td className="border px-2 py-1 font-semibold">ÿ¢ŸÖÿ± ÿ®ÿ±ŸÜÿßŸÖŸá ÿ™ÿ±€åŸÜŸÜ⁄Ø</td>
              <td className="border px-2 py-1 font-semibold">ÿ±ÿ¶€åÿ≥ ÿ¥ŸÅÿßÿÆÿßŸÜŸá</td>
            </tr>
            <tr>
              <td className="border px-2 py-1">{form.departmentHead || ""}</td>
              <td className="border px-2 py-1">{form.programHead || ""}</td>
              <td className="border px-2 py-1">{form.hospitalHead || ""}</td>
            </tr>
          </tbody>
        </table>
      </div>

      <div className="space-x-2 mt-4">
        <button onClick={handlePrint} className="bg-blue-500 text-white px-3 py-1 rounded">⁄ÜÿßŸæ PDF</button>
        <button onClick={exportExcel} className="bg-green-500 text-white px-3 py-1 rounded">⁄ÜÿßŸæ Excel</button>
        <button onClick={onClose} className="bg-gray-500 text-white px-3 py-1 rounded">ÿ®ÿ≥ÿ™ŸÜ</button>
      </div>
    </div>
  );
}

