      { id: "individual", title: "ÿ±ÿ≥€åÿØ⁄Ø€å ÿ®Ÿá Ÿàÿ∂ÿπ ŸÅÿ±ÿØ€å", percent: 2 },
      { id: "social", title: "ÿ±ÿßÿ®ÿ∑Ÿá ÿßÿ¨ÿ™ŸÖÿßÿπ€å", percent: 2 },
      { id: "position", title: "ÿßÿ≥ÿ™ŸÅÿßÿØŸá ÿ®ÿ¨ÿß ÿßÿ≤ ŸÖŸàŸÇŸÅ ⁄©ÿßÿ±€å", percent: 2 },
    ],
  },
];

const months = Array.from({ length: 12 }, (_, i) => i + 1);

const ChecklistForm: React.FC = () => {
  const [studentName, setStudentName] = useState("");
  const [fatherName, setFatherName] = useState("");
  const [year, setYear] = useState("");
  const [scores, setScores] = useState<Record<string, Record<number, number>>>(
    {}
  );

  const handleScoreChange = (
    activityId: string,
    month: number,
    value: number
  ) => {
    setScores((prev) => ({
      ...prev,
      [activityId]: {
        ...prev[activityId],
        [month]: value,
      },
    }));
  };

  const calculateTotal = (activity: Activity) => {
    const monthlyScores = scores[activity.id] || {};
    return Object.values(monthlyScores).reduce((sum, v) => sum + (v || 0), 0);
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    const dataToSave = {
      studentName,
      fatherName,
      year,
      sections: sections.map((sec) => ({
        name: sec.name,
        activities: sec.activities.map((act) => {
          const monthlyScores = months.map((m) => ({
            month: m,
            value: scores[act.id]?.[m] || 0,
          }));
          return {
            id: act.id,
            title: act.title,
            percent: act.percent,
            months: monthlyScores,
            total: monthlyScores.reduce((sum, s) => sum + s.value, 0),
          };
        }),
      })),
    };

    try {
      const res = await fetch("http://localhost:5000/api/checklists", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify(dataToSave),
      });
      if (!res.ok) throw new Error("Failed to save");
      alert("‚úÖ ÿ∞ÿÆ€åÿ±Ÿá ÿ¥ÿØ!");
    } catch (err) {
      console.error(err);
      alert("‚ùå ÿÆÿ∑ÿß ÿØÿ± ÿ∞ÿÆ€åÿ±Ÿá");
    }
  };

  return (
    <>
      {/* ÿπŸÜŸàÿßŸÜ ÿ´ÿßÿ®ÿ™ ÿ®ÿßŸÑÿß */}
      <h1 className="text-2xl font-bold mb-4 text-center">
        ⁄Ü⁄© ŸÑ€åÿ≥ÿ™ ⁄©ÿßÿ±€å Ÿà ÿßÿ±ÿ≤€åÿßÿ®€å ŸÖÿßŸáŸàÿßÿ± ÿ™ÿ±€åŸÜ€å‚ÄåŸáÿß
      </h1>

      <form
        onSubmit={handleSubmit}
        className="max-w-7xl mx-auto p-6 bg-gray-100 rounded-xl shadow-md min-h-[90vh] overflow-auto"
      >
        <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
          <input
            type="text"
            placeholder="ŸÜÿßŸÖ ÿ™ÿ±€åŸÜ€å"
            value={studentName}
            onChange={(e) => setStudentName(e.target.value)}
            className="border px-3 py-2 rounded-lg"
            required
          />
          <input
            type="text"
            placeholder="ŸàŸÑÿØ"
            value={fatherName}
            onChange={(e) => setFatherName(e.target.value)}
            className="border px-3 py-2 rounded-lg"
            required
          />
          <input
            type="text"
            placeholder="ÿ≥ÿßŸÑ ÿ¢ŸÖŸàÿ≤ÿ¥€å"
            value={year}
            onChange={(e) => setYear(e.target.value)}
            className="border px-3 py-2 rounded-lg"
            required
          />
        </div>

        {/* ÿ¨ÿØŸàŸÑ‚ÄåŸáÿß */}
        <div className="overflow-auto max-h-[70vh]">
          {sections.map((section) => (
            <div key={section.name} className="mb-10 min-w-[900px]">
              <h2 className="text-lg font-semibold mb-2">{section.name}</h2>
              <table className="w-full border text-center text-sm bg-white rounded-lg shadow-sm">
                <thead>
                  <tr className="bg-gray-200">
                    <th className="p-2 border">ŸÅÿπÿßŸÑ€åÿ™</th>
                    <th className="p-2 border">ŸÅ€åÿµÿØ€å</th>
                    {months.map((m) => (
                      <th key={m} className="p-2 border">
                        {m}
                      </th>
                    ))}
                    <th className="p-2 border">ŸÖÿ¨ŸÖŸàÿπŸá ŸÜŸÖÿ±ÿßÿ™</th>
                  </tr>
                </thead>
                <tbody>
                  {section.activities.map((act) => (
                    <tr key={act.id}>
                      <td className="p-2 border">{act.title}</td>
                      <td className="p-2 border">{act.percent}%</td>
                      {months.map((m) => (
                        <td key={m} className="p-2 border">
                          <input
                            type="number"
                            min={0}
                            max={act.percent}
                            value={scores[act.id]?.[m] || ""}
                            onChange={(e) =>
                              handleScoreChange(
                                act.id,
                                m,
                                Number(e.target.value)
                              )
                            }
                            className="w-16 border rounded text-center"
                          />
                        </td>
                      ))}
                      <td className="p-2 border font-bold">
                        {calculateTotal(act)}
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          ))}
        </div>

        <button
          type="submit"
          className="mt-6 w-full bg-blue-600 text-white py-2 rounded-lg hover:bg-blue-700 transition"
        >
          ÿ∞ÿÆ€åÿ±Ÿá
        </button>
      </form>
    </>
  );
};

export default ChecklistForm;

