import { Request, Response } from "express";
import { EvaluationFormG } from "../models/form-G";

// ÿ∞ÿÆ€åÿ±Ÿá ŸÅÿ±ŸÖ ÿ¨ÿØ€åÿØ
export const createEvaluationFormG = async (req: Request, res: Response) => {
  try {
    const { trainerId, personalInfo, scores } = req.body;

    if (!trainerId) return res.status(400).json({ message: "ÿ¥ŸÜÿßÿ≥Ÿá ÿ™ÿ±€åŸÜÿ± ÿßŸÑÿ≤ÿßŸÖ€å ÿßÿ≥ÿ™." });
    if (!personalInfo || !scores) return res.status(400).json({ message: "ÿßÿ∑ŸÑÿßÿπÿßÿ™ ⁄©ÿßŸÖŸÑ ŸÅÿ±ŸÖ ÿßŸÑÿ≤ÿßŸÖ€å ÿßÿ≥ÿ™." });

    // ŸÖÿ≠ÿßÿ≥ÿ®Ÿá ŸÖ€åÿßŸÜ⁄Ø€åŸÜ ⁄©ŸÑ (averageScore)
    const filledRows = scores.slice(0, 5);
    const totalSum = filledRows.reduce((sum: number, row: any) => sum + (Number(row.total) || 0), 0);
    const averageScore = totalSum / filledRows.length;

    const form = new EvaluationFormG({
      trainer: trainerId,
      personalInfo,
      scores,
      averageScore,
    });

    await form.save();
    res.status(201).json({ message: "ŸÅÿ±ŸÖ ÿ®ÿß ŸÖŸàŸÅŸÇ€åÿ™ ÿ∞ÿÆ€åÿ±Ÿá ÿ¥ÿØ", form });
  } catch (err) {
    console.error(err);
    res.status(500).json({ message: "ÿÆÿ∑ÿß ÿØÿ± ÿ∞ÿÆ€åÿ±Ÿá ŸÅÿ±ŸÖ", error: err });
  }
};

// ÿØÿ±€åÿßŸÅÿ™ ŸÅÿ±ŸÖ‚ÄåŸáÿß ÿ®ÿ± ÿßÿ≥ÿßÿ≥ trainerId
export const getEvaluationFormsByTrainer = async (req: Request, res: Response) => {
  try {
    const { trainerId } = req.params;

    if (!trainerId) return res.status(400).json({ message: "ÿ¥ŸÜÿßÿ≥Ÿá ÿ™ÿ±€åŸÜÿ± ÿßŸÑÿ≤ÿßŸÖ€å ÿßÿ≥ÿ™." });

    const forms = await EvaluationFormG.find({ trainer: trainerId }).sort({ createdAt: -1 });
    res.status(200).json(forms);
  } catch (err) {
    console.error(err);
    res.status(500).json({ message: "ÿÆÿ∑ÿß ÿØÿ± ÿØÿ±€åÿßŸÅÿ™ ŸÅÿ±ŸÖ‚ÄåŸáÿß", error: err });
  }
};

